INSERT INTO `product` VALUES (1, '[999]感冒灵颗粒10g*9袋/盒', 14.98, '解热镇痛。用于感冒引起的头痛，发热，鼻塞，流涕，咽痛。', '1639953327152844802', 9, 2, 1);
INSERT INTO `product` VALUES (2, '京都念慈菴蜜炼川贝枇杷膏300ml*1瓶/盒', 39.80, '润肺化痰、止咳平喘、护喉利咽、生津补气、调心降火。本品适用于伤风咳嗽、痰稠、痰多气喘、咽喉干痒及声音嘶哑。', '1640577946536865794', 1, 2, 0);
INSERT INTO `product` VALUES (3, '以岭连花清瘟胶囊0.35g*24粒/盒', 14.80, '清瘟解毒，宣肺泄热。用于治疗流行性感冒属热毒袭肺证，症见：发热或高热，恶寒，肌肉酸痛，鼻塞流涕，咳嗽，头痛，咽干咽痛，舌偏红，苔黄或黄腻等。', '1640577990749024258', 1, 2, 0);
INSERT INTO `product` VALUES (4, '白云山板蓝根颗粒10g*20袋/包', 19.80, '清热解毒，凉血利咽。用于肺胃热盛所致的咽喉肿痛、口咽干燥；急性扁桃体炎见上述证候者。', '1640578024827744258', 1, 2, 0);
INSERT INTO `product` VALUES (5, '同仁堂二陈丸6g*12袋/盒', 29.40, '燥湿化痰，理气和胃。用于痰湿停滞导致的咳嗽痰多，胸脘胀闷，恶心呕吐。', '1640578061565652993', 1, 2, 0);
INSERT INTO `product` VALUES (7, '[养无极]补肺丸9g*40丸/盒', 358.00, '补肺益气，止咳平喘。用于肺气不足，气短喘咳，咳声低弱，干咳痰粘，咽干舌燥。', '1646815069668085761', 2, 10, 0);
INSERT INTO `product` VALUES (8, '[金笛]复方鱼腥草合剂10ml*18瓶/盒', 45.00, '清热解毒。用于外感风热引起的咽喉疼痛；急性咽炎、扁桃腺炎有风热证候者。', '1646816120353820673', 5, 10, 0);
INSERT INTO `product` VALUES (9, '[真奥]金银花口服液20ml*6支/盒', 23.99, '清热解毒、疏散风热。用于暑热内犯所致的中暑、痱疹、疖肿、风热感冒、症见发热口渴、咽喉肿痛、痱疹鲜红。', '1646816818185342978', 3, 11, 0);
INSERT INTO `product` VALUES (10, '[小葵花]小儿肺热咳喘颗粒3g*6袋/盒', 7.45, '清热解毒，宣肺止咳，化咳平喘。用于感冒，支气管炎属痰热壅肺证者.', '1646817958876647426', 6, 12, 0);
INSERT INTO `product` VALUES (11, '[普安康]宣肺止嗽合剂20ml*6支/盒', 27.40, '疏风宣肺，止咳化痰。用于咳嗽属风邪犯肺证，症见咳嗽、咽痒、鼻塞流涕、恶寒发热、咯痰等。', '1646818924963270658', 3, 13, 0);
INSERT INTO `product` VALUES (12, '[北京同仁堂]通宣理肺丸6g*10丸/盒', 11.90, '解表散寒,宣肺止嗽。用于风寒束表、肺气不宣所致的感冒咳嗽,症见发热、恶寒、咳嗽、鼻塞流涕、头痛、无汗、肢体酸痛', '1646819493811556353', 2, 13, 0);
INSERT INTO `productbasicinfo` VALUES (1, 1, '感冒灵颗粒', '999', '华润三九医药股份有限公司', '国药准字Z44021940', '10g*9袋/盒', '颗粒', '口服', '一次10克（1袋），一日3次', '24个月', '不限', '【药品名称】\n通用名称：感冒灵颗粒\n商品名称：999\n英文名称：GanMaoLingKeLi(999)\n汉语拼音：Ganmaoling Keli\n【成份】三叉苦、金盏银盘、野菊花、岗梅、咖啡因、对乙酰氨基酚、马来酸氯苯那敏、薄荷油。辅料为蔗糖粉。\n【性状】本品为浅棕色至深棕色颗粒;味甜、微苦。\n【功能主治】解热镇痛。用于感冒引起的头痛，发热，鼻塞。流涕，咽痛等。\n【用法用量】开水冲服。一次10克(1袋)，一日3次。\n【不良反应】偶见皮疹、荨麻疹、药热及粒细胞减少；可见困倦、嗜睡、口渴、虚弱感；长期大量用药会导致肝肾功能异常。\n【禁忌】严重肝肾功能不全者禁用。\n【注意事项】\n1.忌烟、酒及辛辣、生冷、油腻食物。\n2.不宜在服药期间同时服用滋补性中成药。\n3.本品含对乙酰氨基酚、马来酸氯苯那敏、咖啡因。服用本品期间不得饮酒或含有酒精的饮料；不能同时服用与本品成份相似的其他抗感冒药；肝、肾功能不全者慎用；膀胱颈梗阻、甲状腺功能亢进、青光眼、高血压和前列腺肥大者慎用；孕妇及哺乳期妇女慎用；服药期间不得驾驶机、车、船、从事高空作业、机械作业及操作精密仪器。\n4.脾胃虚寒，症见腹痛、喜暖、泄泻者慎用。\n5.糖尿病患者及有心脏病等慢性病严重者应在医师指导下服用。\n6.儿童、年老体弱者应在\n【药物相互作用】\n1、与其他解热镇痛药并用，有增加肾毒性的危险。\n2、与其他药物同时使用可能会发生药物相互作用，详情请咨询医师或药师。\n【药理作用】尚不明确\n【贮藏】密封\n【规格】每袋装10克(含对乙酰氨基酚0.2克)\n【包装规格】每盒装9袋，复合膜\n【有效期】24个月\n【批准文号】国药准字Z44021940\n【说明书修订日期】2007年05月15日\n【生产企业】\n企业名称：华润三九医药股份有限公司\n企业简称：华润三九');
