INSERT INTO `typhoidtheorychineseherbcontent` VALUES (1, 1, 4, '《神农本草经》\n【旋复花】味咸。温。主治结气。胁下满。惊悸。除水。去五藏间寒热。补中下气。一名金沸草。一名盛椹。生平泽。');
INSERT INTO `typhoidtheorychineseherbcontent` VALUES (2, 1, 4, '《名医别录》\n【旋复花】味甘。微温。冷利。有小毒。消胸上痰结唾如胶漆。心胁痰水。膀胱留饮。风气湿痹。皮间死肉。目中眵䁾。利大肠。通血脉。益色泽。一名戴椹。\n【根】主风湿。生平泽。五月采花。日干。廿日成。');
INSERT INTO `typhoidtheorychineseherbcontent` VALUES (3, 1, 1, '<fj>旋覆代赭汤</fj>（三两）');
INSERT INTO `typhoidtheorychineseherbcontent` VALUES (4, 1, 2, '<fj>旋覆花汤</fj>（三两）');
INSERT INTO `typhoidtheorychineseherbcontent` VALUES (5, 3, 4, '《神农本草经》\n【麦门冬】味甘。平。主治心腹结气。伤中。伤饱。胃络脉绝。羸瘦。短气。久服轻身。不老。不饥。生函谷川谷。');
INSERT INTO `typhoidtheorychineseherbcontent` VALUES (6, 3, 4, '《名医别录》\n【麦门冬】微寒。无毒。主治身重目黄。心下支满。虚劳客热。口干燥渴。止呕吐。愈痿蹶。强阴。益精。消谷调中。保神。定肺气。安五藏。令人肥健。美颜色。有子。秦名羊韭。齐名爱韭。楚名乌韭。越名羊蓍。一名禹葭。一名禹余粮。叶如韭。冬夏长生。生函谷及堤坂肥土石间久废处。二月三月八月十月采。阴干。（地黄车前为之使。恶款冬苦瓠。畏苦参青蘘。）');
INSERT INTO `typhoidtheorychineseherbcontent` VALUES (7, 3, 1, '<fj>竹叶石膏汤</fj>（一斤）');
INSERT INTO `typhoidtheorychineseherbcontent` VALUES (8, 3, 1, '<fj>炙甘草汤</fj>（半升）');
INSERT INTO `typhoidtheorychineseherbcontent` VALUES (9, 3, 2, '<fj>薯蓣丸</fj>（六分）');
INSERT INTO `typhoidtheorychineseherbcontent` VALUES (10, 3, 2, '<fj>麦门冬汤</fj>（七升）');
INSERT INTO `typhoidtheorychineseherbcontent` VALUES (11, 3, 2, '<fj>温经汤</fj>（一升）');
INSERT INTO `typhoidtheorychineseherbcontent` VALUES (37, 10, 4, '《神农本草经》\n【蛇床子】味苦。平。主治妇人阴中肿痛。男子阴痿湿痒。除痹气。利关节。治癫痫。恶疮。久服轻身。一名蛇粟。一名蛇米。生临淄川谷。');
INSERT INTO `typhoidtheorychineseherbcontent` VALUES (38, 10, 4, '《名医别录》\n【蛇床子】味辛甘。无毒。主温中下气。令妇人子藏热。男子阴强。久服好颜色。令人有子。一名虺床。一名思益。一名绳毒。一名枣棘。一名墙蘼。生临淄。五月采实。阴干。（恶牡丹巴豆贝母。）');
INSERT INTO `typhoidtheorychineseherbcontent` VALUES (40, 11, 4, '《开宝本草》\n【红蓝花】味辛。温。无毒。主产后血运口噤。腹内恶血不尽。绞痛。胎死腹中。并酒煮服。亦主蛊毒下血。堪作燕脂。生梁汉及西域。一名黄蓝。');
INSERT INTO `typhoidtheorychineseherbcontent` VALUES (41, 11, 2, '<fj>红蓝花酒</fj>（一两）');
INSERT INTO `typhoidtheorychineseherbcontent` VALUES (42, 12, 4, '《神农本草经》\n【王瓜】味苦。寒。主治消渴。内痹。瘀血。月闭。寒热。酸疼。益气。愈聋。一名土瓜。生鲁地平泽。');
INSERT INTO `typhoidtheorychineseherbcontent` VALUES (43, 12, 4, '《名医别录》\n【王瓜】无毒。主治诸邪气。热结。鼠瘘。散痈肿留血。妇人带下不通。下乳汁。止小便数不禁。逐四肢骨节中水。治马骨刺入疮。生鲁地田野及人家垣墙间。三月采根。阴干。');
INSERT INTO `typhoidtheorychineseherbcontent` VALUES (44, 12, 4, '《药性论》\n【土瓜根】使。平。一名王瓜。子主蛊毒。治小便数。遗不禁。');
INSERT INTO `typhoidtheorychineseherbcontent` VALUES (45, 12, 2, '<fj>土瓜根散</fj>（三分）');
INSERT INTO `typhoidtheorychineseherbcontent` VALUES (46, 13, 4, '《名医别录》\n【苏】味辛。温。主下气。除寒中。其子尤良。');
INSERT INTO `typhoidtheorychineseherbcontent` VALUES (47, 13, 4, '《药性论》\n【紫苏子】无毒。主上气咳逆。治冷气及腰脚中湿风结气。将子研汁煮粥良。长服令人肥白身香。和高良姜橘皮等分蜜丸。空心下十丸。下一切宿冷气。及脚湿气。');
INSERT INTO `typhoidtheorychineseherbcontent` VALUES (48, 13, 4, '《药性论》\n【紫苏叶】可生食。与一切鱼肉作羹。良。');
INSERT INTO `typhoidtheorychineseherbcontent` VALUES (49, 14, 4, '《神农本草经》\n【柏子】味甘。平。无毒。主治惊悸。安五藏。益气。除风湿痹。久服令人润泽美色。耳目聪明。不饥。不老。轻身。延年。生太山山谷。');
INSERT INTO `typhoidtheorychineseherbcontent` VALUES (50, 14, 4, '《名医别录》\n【柏实】无毒。主治恍惚虚损。吸吸历节。腰中重痛。益血。止汗。生太山。柏叶尤良。');
INSERT INTO `typhoidtheorychineseherbcontent` VALUES (51, 15, 4, '《神农本草经》\n【白薇】味苦。平。主治暴中风。身热。肢满。忽忽不知人。狂惑邪气。寒热酸疼。温疟洗洗发作有时。生平原川谷。');
INSERT INTO `typhoidtheorychineseherbcontent` VALUES (52, 15, 4, '《名医别录》\n【白薇】味咸。大寒。无毒。主治伤中淋露。下水气。利阴气。益精。一名白幕。一名薇草。一名春草。一名骨美。久服利人。生平原。三月三日采根。阴干。（恶黄芪干姜干漆山茱萸大枣。）');
INSERT INTO `typhoidtheorychineseherbcontent` VALUES (53, 15, 2, '<fj>竹皮大丸</fj>（一分(有热者倍之)）');
INSERT INTO `typhoidtheorychineseherbcontent` VALUES (54, 16, 4, '《神农本草经》\n【竹叶】味苦。平。主治咳逆上气。溢筋急。恶疡。杀小虫。【根】作汤。益气。止渴。补虚。下气。【汁】治风痓痹。【实】通神明。轻身。益气。生益州。');
INSERT INTO `typhoidtheorychineseherbcontent` VALUES (55, 16, 4, '《名医别录》\n【芹竹叶】大寒。无毒。主除烦热。风痓。喉痹。呕逆。【根】消毒。生益州。');
INSERT INTO `typhoidtheorychineseherbcontent` VALUES (56, 16, 4, '《名医别录》\n【淡竹叶】味辛。平。大寒。主治胸中淡热。咳逆上气。其【沥】大寒。治暴中风。风痹。胸中大热。止烦闷。其【皮茹】微寒。主治呕啘。温气寒热。吐血。崩中。溢筋。');
INSERT INTO `typhoidtheorychineseherbcontent` VALUES (57, 16, 4, '《名医别录》\n【苦竹叶及沥】治口疮。目痛明目。通利九窍。【竹笋】味甘。无毒。主消渴。利水道。益气。可久食。【干笋】烧服。治五痔血。');
INSERT INTO `typhoidtheorychineseherbcontent` VALUES (58, 16, 1, '<fj>竹叶石膏汤</fj>（二把）');
INSERT INTO `typhoidtheorychineseherbcontent` VALUES (59, 16, 2, '<fj>竹叶汤</fj>（一把）');
INSERT INTO `typhoidtheorychineseherbcontent` VALUES (60, 17, 4, '《神农本草经》\n【冬葵子】味甘。寒。主治五藏六府寒热。羸瘦。五癃。利小便。久服坚骨。长肌肉。轻身。延年。生少室山。');
INSERT INTO `typhoidtheorychineseherbcontent` VALUES (61, 17, 4, '《名医别录》\n【冬葵子】无毒。主治妇人乳难内闭。生少室。十二月采。（黄芩为之使。）');
INSERT INTO `typhoidtheorychineseherbcontent` VALUES (62, 17, 4, '《本草经集注》\n【冬葵子】至滑利。能下石。');
INSERT INTO `typhoidtheorychineseherbcontent` VALUES (63, 17, 4, '《药性论》\n【冬葵子】臣。滑。平。能治五淋。主奶肿。能下乳汁。');
INSERT INTO `typhoidtheorychineseherbcontent` VALUES (64, 17, 2, '<fj>葵子茯苓散</fj>（一斤）');
INSERT INTO `typhoidtheorychineseherbcontent` VALUES (65, 18, 4, '《神农本草经》\n【梅实】味酸。平。主下气。除热烦满。安心。肢体痛。偏枯。不仁死肌。去青黑痣恶疾。生汉中川谷。');
INSERT INTO `typhoidtheorychineseherbcontent` VALUES (66, 18, 4, '《名医别录》\n【梅实】无毒。止下痢。好唾。口干。生漠中。五月采。火干。又【梅根】疗风痹。出土者杀人。【梅实】利筋脉。去痹。');
INSERT INTO `typhoidtheorychineseherbcontent` VALUES (67, 18, 4, '《本草图经》\n五月采其黄实。火熏干。作乌梅。');
INSERT INTO `typhoidtheorychineseherbcontent` VALUES (68, 18, 4, '《本草衍义》\n燻之为乌梅。暴干藏密器中为白梅。');
INSERT INTO `typhoidtheorychineseherbcontent` VALUES (69, 18, 1, '<fj>乌梅丸</fj>（300枚）');
INSERT INTO `typhoidtheorychineseherbcontent` VALUES (70, 19, 4, '《名医别录》\n【青梁米】味甘。微寒。无毒。主治胃瘅。热中。消渴。止泄痢。利小便。益气。补中。轻身。长年。');
INSERT INTO `typhoidtheorychineseherbcontent` VALUES (71, 19, 4, '《名医别录》\n【黄梁米】味甘。平。无毒。主益气。和中。止泄。');
INSERT INTO `typhoidtheorychineseherbcontent` VALUES (72, 19, 4, '《名医别录》\n【白梁米】味甘。微寒。无毒。主除热。益气。');
INSERT INTO `typhoidtheorychineseherbcontent` VALUES (73, 19, 1, '猪肤汤（五合）');
INSERT INTO `typhoidtheorychineseherbcontent` VALUES (74, 20, 4, '《名医别录》\n【蜘蛛】微寒。主治大人小儿㿉。七月七日取其网。治喜忘。又。疗小儿大腹丁奚。三年不能行者。');
INSERT INTO `typhoidtheorychineseherbcontent` VALUES (75, 20, 2, '<fj>蜘蛛散</fj>（14枚）');
INSERT INTO `typhoidtheorychineseherbcontent` VALUES (76, 21, 4, '《神农本草经》\n【鸡屎白】主治消渴。伤寒。寒热。');
INSERT INTO `typhoidtheorychineseherbcontent` VALUES (77, 21, 4, '《名医别录》\n【鸡矢白】微寒。破石淋及转筋。利小便。止遗溺。灭瘢痕。');
INSERT INTO `typhoidtheorychineseherbcontent` VALUES (78, 21, 2, '<fj>鸡屎白散</fj>（方寸匕）');
INSERT INTO `typhoidtheorychineseherbcontent` VALUES (79, 22, 4, '《神农本草经》\n【藜芦】味辛。寒。主治蛊毒。咳逆。泄痢。肠澼。头疡。疥瘙。恶疮。杀诸虫毒。去死肌。一名葱苒。生太山山谷。');
INSERT INTO `typhoidtheorychineseherbcontent` VALUES (80, 22, 4, '《名医别录》\n【藜芦】味苦。微寒。有毒。主治啘逆。喉痹不通。鼻中息肉。马刀烂疮。不入汤。一名葱菼。一名山葱。生太山。三月采根。阴干。（黄连为之使。反细辛芍药五参。恶大黄。）');
INSERT INTO `typhoidtheorychineseherbcontent` VALUES (81, 22, 2, '<fj>藜芦甘草汤</fj>（三两）');
INSERT INTO `typhoidtheorychineseherbcontent` VALUES (82, 23, 4, '《名医别录》\n【蒴藋】味酸。温。有毒。主治风瘙瘾疹身痒湿痹。可作浴汤。一名堇草。一名芨。生田野。春夏采叶。秋冬采茎根。');
INSERT INTO `typhoidtheorychineseherbcontent` VALUES (83, 23, 2, '<fj>王不留行散</fj>（十分）');
INSERT INTO `typhoidtheorychineseherbcontent` VALUES (84, 24, 4, '《神农本草经》\n【王不留行】味苦。主治金创。止血。逐痛。出刺。除风痹内寒。久服轻身。耐老。增寿。生太山山谷。');
INSERT INTO `typhoidtheorychineseherbcontent` VALUES (85, 24, 4, '《名医别录》\n【王不留行】味甘。平。无毒。止心烦。鼻衄。痈疽。恶疮。瘘乳。妇人难产。生太山。二月八月采。');
INSERT INTO `typhoidtheorychineseherbcontent` VALUES (86, 24, 2, '<fj>王不留行散</fj>（十分）');
INSERT INTO `typhoidtheorychineseherbcontent` VALUES (87, 25, 4, '《神农本草经》\n【桑根白皮】味甘。寒。主治伤中。五劳。六极。羸瘦。崩中。脉绝。补虚。益气。生犍为山谷。');
INSERT INTO `typhoidtheorychineseherbcontent` VALUES (88, 25, 4, '《名医别录》\n【桑根白皮】无毒。主去肺中水气。止唾血。热渴。水肿。腹满。胪胀。利水道。去寸白。可以缝金创。采无时。出土上者杀人。（续断桂心麻子为之使。叶汁解蜈蚣毒。）');
INSERT INTO `typhoidtheorychineseherbcontent` VALUES (89, 25, 4, '《药性论》\n【桑白皮】使。平。能治肺气喘满。水气浮肿。主伤绝。利水道。消水气。虚劳客热头痛。内补不足。');
INSERT INTO `typhoidtheorychineseherbcontent` VALUES (90, 25, 2, '<fj>王不留行散</fj>（十分）');
INSERT INTO `typhoidtheorychineseherbcontent` VALUES (91, 26, 4, '《神农本草经》\n【败酱】味苦。平。主治暴热。火疮。赤气。疥瘙。疽。痔。马鞍热气。一名鹿肠。生江夏川谷。');
INSERT INTO `typhoidtheorychineseherbcontent` VALUES (92, 26, 4, '《名医别录》\n【败酱】味咸。微寒。无毒。主除痈肿。浮肿。结热。风痹。不足。产后疾痛。一名鹿首。名马草。一名泽败。生江夏。八月采根。暴干。');
INSERT INTO `typhoidtheorychineseherbcontent` VALUES (93, 26, 2, '<fj>薏苡附子败酱散</fj>（五分）');
INSERT INTO `typhoidtheorychineseherbcontent` VALUES (94, 27, 4, '《新修本草》\n【诃梨勒】味苦。温。无毒。主冷气心腹胀满。下宿物。生交爱州。');
INSERT INTO `typhoidtheorychineseherbcontent` VALUES (95, 27, 2, '<fj>诃梨勒散</fj>（10枚）');
INSERT INTO `typhoidtheorychineseherbcontent` VALUES (96, 28, 4, '《名医别录》\n【伏龙肝】味辛。微温。主治妇人崩中。吐下血。止咳逆。止血。消痈肿毒气。');
INSERT INTO `typhoidtheorychineseherbcontent` VALUES (97, 28, 4, '《药性论》\n【伏龙肝】单用亦可。味咸。无毒。末与醋调。涂痈肿。');
INSERT INTO `typhoidtheorychineseherbcontent` VALUES (98, 29, 4, '《名医别录》\n【马通】微温。治妇人崩中。止渴。及吐下血。鼻衄。金创。止血。');
INSERT INTO `typhoidtheorychineseherbcontent` VALUES (99, 29, 2, '<fj>柏叶汤</fj>（一升）');
INSERT INTO `typhoidtheoryprescription` VALUES (1, '桂枝加大黄汤', 1, '<zy>桂枝</zy>三两。去皮 <zy>大黄</zy>二两 <zy>芍药</zy>六两 <zy>甘草</zy>二两。炙 <zy>生姜</zy>三两。切 大枣十二枚。擘', '上六味。以水七升。煮取三升。去滓。温服一升。日三服。');
INSERT INTO `typhoidtheoryprescription` VALUES (2, '小儿疳虫蚀齿', 2, '<zy>雄黄</zy>，<zy>葶苈</zy>', '右二味，末之，取腊月猪脂镕，以<zy>槐枝</zy>绵裹头四五枚，点药烙之。');
INSERT INTO `typhoidtheoryprescription` VALUES (3, '旋覆代赭汤', 1, '<zy>旋覆花</zy>三两 <zy>人参</zy>二两 <zy>生姜</zy>五两 <zy>代赭石</zy>一两 <zy>甘草</zy>三两。炙 <zy>半夏</zy>半升(洗) 大枣十二枚。擘', '上七味。以水一斗。煮取六升。去滓。再煎取三升。温服一升。日三服。');
INSERT INTO `typhoidtheoryprescription` VALUES (7, '麻黄连轺赤小豆汤', 1, '麻黄二两。去节 连轺二两(连翘根) 杏仁四十个。去皮尖 赤小豆一升 大枣十二枚。擘 生梓白皮一升。切 生姜二两。切 甘草二两。炙', '上八味。以潦水一斗。先煮麻黄。再沸。去上沫。内诸药。煮取三升。分温三服。半日服尽。');
INSERT INTO `typhoidtheoryprescription` VALUES (8, '枳实栀子豉汤', 1, '枳实三枚。炙 栀子十四个。擘 豉一升(绵裹)', '上三味。以清浆水七升。空煮取四升。内枳实、栀子。煮取二升。下豉。更煮五六沸。去滓。温分再服。覆令微似汗。若有宿食者。内大黄如博棋子大五六枚。服之愈。');
INSERT INTO `typhoidtheoryprescription` VALUES (9, '炙甘草汤', 1, '甘草四两。炙 生姜三两。切 人参二两 桂枝三两。去皮 生地黄一斤 阿胶二两 麦门冬半升(去心) 麻子仁半升 大枣三十枚。擘', '上九味。以清酒七升。水八升。先煮八味。取三升。去滓。内胶烊消尽。温服一升。日三服。一名复脉汤。');
INSERT INTO `typhoidtheoryprescription` VALUES (10, '四逆汤', 1, '甘草二两。炙 干姜一两半 附子一枚。生用去皮破八片', '上三味。以水三升。煮取一升二合。去滓。分温再服。强人可大附子一枚 干姜三两。');
INSERT INTO `typhoidtheoryprescription` VALUES (11, '竹叶石膏汤', 1, '竹叶二把 石膏一斤 半夏半升(洗) 麦门冬一斤(去心) 人参二两 甘草二两。炙 粳米半斤', '上七味。以水一斗。煮取六升。去滓。内粳米。煮米熟汤成。去米。温服一升。日三服。');
INSERT INTO `typhoidtheoryprescription` VALUES (12, '通脉四逆加猪胆汁汤', 1, '甘草二两。炙 附子大者一枚(生。去皮。破八片) 干姜三两。强人可四两 猪胆汁半合', '上四味。以水三升。煮取一升二合。去滓。内猪胆汁。分温再服。其脉即来。无猪胆。以羊胆代之。');
INSERT INTO `typhoidtheoryprescription` VALUES (13, '白头翁汤', 1, '白头翁二两 黄柏三两 黄连三两 秦皮三两', '上四味。以水七升。煮取二升。去滓。温服一升。不愈。更服一升。');
INSERT INTO `typhoidtheoryprescription` VALUES (14, '四逆散', 1, '甘草炙 枳实(破。水渍。炙干) 芍药 柴胡', '上四味。各十分。捣筛。白饮和服方寸匕。日三服。咳者。加五味子、干姜各五分。并主下利。悸者。加桂枝五分。小便不利者。加茯苓五分。腹中痛者。加附子一枚。炮令坼。泄利下重者。先以水五升。煮薤白三升。去滓。以散三方寸匕。内汤中。煮取一升半。分温再服。');
INSERT INTO `typhoidtheoryprescription` VALUES (15, '牡蛎泽泻散', 1, '牡蛎(熬) 泽泻 蜀漆(煖水洗。去腥) 葶苈子(熬) 商陆根(熬) 海藻(洗。去咸) 栝楼根各等分', '上七味。异捣。下筛为散。更于臼中治之。白饮和服方寸匕。日三服。小便利。止后服。');
INSERT INTO `typhoidtheoryprescription` VALUES (16, '烧裈散', 1, '妇人中裈近隐处取烧作灰', '上一味。水服方寸匕。日三服。小便即利。阴头微肿。此为愈矣。妇人病取男子裈烧服。');
INSERT INTO `typhoidtheoryprescription` VALUES (17, '理中丸', 1, '人参 干姜 甘草炙 白术各三两', '上四味。捣筛。蜜合为丸。如鸡子黄大。以沸汤数合。和一丸。研碎。温服之。日三四、夜二服。腹中未热。益至三四丸。然不及汤。汤法：以四物依两数切。用水八升。煮取三升。去滓。温服一升。日三服。若脐上筑者。肾气动也。去术加桂四两。吐多者。去术加生姜三两。下多者还用术。悸者。加茯苓二两。渴欲得水者。加术。足前成四两半。腹中痛者。加人参。足前成四两半。寒者。加干姜。足前成四两半。腹满者。去术。加附子一枚。服汤后。如食顷。饮热粥一升许。微自温。勿发揭衣被。');
INSERT INTO `typhoidtheoryprescription` VALUES (18, '四逆加人参汤', 1, '甘草二两。炙 附子一枚(生。去皮。破八片) 干姜一两半 人参一两', '上四味。以水三升。煮取一升二合。去滓。分温再服。');
INSERT INTO `typhoidtheoryprescription` VALUES (19, '干姜黄芩黄连人参汤', 1, '干姜 黄芩 黄连 人参各三两', '上四味。以水六升。煮取二升。去滓。分温再服。');
INSERT INTO `typhoidtheoryprescription` VALUES (20, '麻黄升麻汤', 1, '麻黄二两半。去节 升麻一两一分 当归一两一分 知母十八铢 黄芩十八铢 葳蕤十八铢(一作菖蒲) 芍药六铢 天门冬六铢(去心) 桂枝六铢。去皮 茯苓六铢 甘草六铢。炙 石膏六铢(碎。绵裹) 白术六铢 干姜六铢', '上十四味。以水一斗。先煮麻黄一两沸。去上沫。内诸药。煮取三升。去滓。分温三服。相去如炊三斗米顷。令尽。汗出愈。');
INSERT INTO `typhoidtheoryprescription` VALUES (21, '当归四逆加吴茱萸生姜汤', 1, '当归三两 桂枝三两。去皮 芍药三两 细辛三两 甘草二两。炙 通草二两 大枣二十五枚。擘 生姜半斤 吴茱萸二升', '上九味。以水六升、清酒六升和。煮取五升。去滓。温分五服。一方。水、酒各四升。');
INSERT INTO `typhoidtheoryprescription` VALUES (22, '当归四逆汤', 1, '当归三两 桂枝三两。去皮 芍药三两 细辛三两 甘草二两。炙 通草二两 大枣二十五枚(擘。一法十二枚)', '上七味。以水八升。煮取三升。去滓。温服一升。日三服。');
INSERT INTO `typhoidtheoryprescription` VALUES (23, '乌梅丸', 1, '乌梅三百枚 细辛六两 干姜十两 黄连十六两 附子六两(炮。去皮) 当归四两 蜀椒四两(出汗) 桂枝六两。去皮 人参六两 黄柏六两', '上十味。异捣筛。合治之。以苦酒（即酸醋）渍乌梅一宿。去核。蒸之五斗米下。饭熟。捣成泥。和药令相得。内臼中。与蜜杵二千下。丸如梧桐子大。先食。饮服十丸。日三服。稍加至二十丸。禁生冷。滑物。臭食等。');
INSERT INTO `typhoidtheoryprescription` VALUES (24, '通脉四逆汤', 1, '甘草二两。炙 附子大者一枚(生用。去皮。破八片) 干姜三两。强人可四两', '上三味。以水三升。煮取一升二合。去滓。分温再服。其脉即出者愈。面色赤者。加葱九茎。腹中痛者。去葱。加芍药二两。呕者。加生姜二两。咽痛者。去芍药。加桔梗一两。利止脉不出者。去桔梗。加人参二两。病皆与方相应者。乃服之。');
INSERT INTO `typhoidtheoryprescription` VALUES (25, '狼牙汤', 2, '狼牙三两', '右一味，以水四升，煮取半升，以绵缠筋如茧，浸汤沥阴中，日四遍。');
INSERT INTO `typhoidtheoryprescription` VALUES (26, '蛇床子散', 2, '蛇床子仁', '右一味，末之，以白粉少许，和令相得，如枣大，绵裹内之，自然温。');
INSERT INTO `typhoidtheoryprescription` VALUES (27, '肾气丸', 2, '干地黄八两，薯蓣四两，山茱萸四两，泽泻三两，茯苓三两，牡丹皮三两，桂枝，附子炮，各一两', '右八味，末之，炼蜜和丸梧子大。酒下十五丸，加至二十五丸，日再服。');
INSERT INTO `typhoidtheoryprescription` VALUES (28, '红蓝花酒', 2, '红蓝花一两', '右一味，以酒一大升，煎减半。顿服一半，未止，再服。');
INSERT INTO `typhoidtheoryprescription` VALUES (29, '矾石丸', 2, '矾石三分，烧，杏仁一分', '右二味，末之，炼蜜和丸枣核大，内脏中，剧者再内之。');
INSERT INTO `typhoidtheoryprescription` VALUES (30, '大黄甘遂汤', 2, '大黄四两，甘遂二两，阿胶二两', '右三味，以水三升，煮取一升。顿服之，其血当下。');
INSERT INTO `typhoidtheoryprescription` VALUES (31, '旋覆花汤', 2, '旋覆花三两，葱十四茎，新绛少许', '右三味，以水三升，煮取一升，顿服之。');
INSERT INTO `typhoidtheoryprescription` VALUES (32, '土瓜根散', 2, '土瓜根，芍药，桂枝，䗪虫各三分', '右四味，杵为散。酒服方寸匕，日三服。');
INSERT INTO `typhoidtheoryprescription` VALUES (33, '温经汤', 2, '吴茱萸三两，当归，芎䓖，芍药各二两，人参，桂枝，阿胶，牡丹去心，生姜，甘草各二两，半夏半升，麦门冬一升，去心', '右十二味，以水一斗，煮取三升，分温三服。亦主妇人少腹寒，久不受胎，兼取崩中去血，或月水来过多，及至期不来。');
INSERT INTO `typhoidtheoryprescription` VALUES (34, '甘麦大枣汤', 2, '甘草三两，小麦一升，大枣十枚', '右三味，以水六升，煮取三升，温分三服。亦补脾气。');
INSERT INTO `typhoidtheoryprescription` VALUES (35, '半夏厚朴汤', 2, '半夏一升，厚朴三两，茯苓四两，生姜五两，干苏叶二两', '右五味，以水七升，煮取四升。分温四服，日三、夜一服。');
INSERT INTO `typhoidtheoryprescription` VALUES (36, '当归建中汤', 2, '当归四两，桂枝三两，芍药六两，生姜三两，甘草二两，大枣十二枚', '右六味，以水一斗，煮取三升。分温三服，一日令尽。若大虚，加饴糖六两，汤成，内之于火上暖，令饴消。若去血过多，崩伤内衄不止，加地黄六两、阿胶二两，合八味，汤成，内阿胶。若无当归，以芎䓖代之；若无生姜，以干姜代之。');
INSERT INTO `typhoidtheoryprescription` VALUES (37, '三物黄芩汤', 2, '黄芩一两，苦参二两，干地黄四两', '右三味，以水八升，煮取二升。温服一升，多吐下虫。');
INSERT INTO `typhoidtheoryprescriptionworkcited` VALUES (1, '279、本太阳病。医反下之。因尔腹满时痛者。属太阴也。<fj>桂枝加芍药汤</fj>主之。大实痛者。<fj>桂枝加大黄汤</fj>主之。', '辨少阴病脉证并治第十一', 1, 1);
INSERT INTO `typhoidtheoryprescriptionworkcited` VALUES (3, '23、<fj>小儿疳虫蚀齿</fj>方 疑非仲景方。', '金匮要略22 妇人杂病', 2, 2);
INSERT INTO `typhoidtheoryprescriptionworkcited` VALUES (4, '161、伤寒发汗。若吐。若下。解后。心下痞硬。噫气不除者。<fj>旋覆代赭汤</fj>主之。', '辨太阳病脉证并治（下）第七', 1, 3);
INSERT INTO `typhoidtheoryprescriptionworkcited` VALUES (5, '35、伤寒发汗。若吐若下。解后。心下痞鞕。噫气不除者。属<fj>旋覆代赭汤</fj>。', '辨发汗吐下后病脉证并治第二十二', 1, 3);
INSERT INTO `typhoidtheoryprescriptionworkcited` VALUES (12, '262、伤寒瘀热在里。身必黄。麻黄连轺赤小豆汤主之。', '辨阳明病脉证并治第八', 1, 7);
INSERT INTO `typhoidtheoryprescriptionworkcited` VALUES (13, '393、大病差后。劳复者。枳实栀子豉汤主之。', '辨阴阳易差后劳复病脉证并治第十四', 1, 8);
INSERT INTO `typhoidtheoryprescriptionworkcited` VALUES (14, '177、伤寒。脉结代。心动悸。炙甘草汤主之。', '辨太阳病脉证并治（下）第七', 1, 9);
INSERT INTO `typhoidtheoryprescriptionworkcited` VALUES (15, '*、附方《千金翼》炙甘草汤 一云复脉汤。治虚劳不足，汗出而闷，脉结悸，行动如常，不出百日，危急者，十一日死。', '金匮要略6 血痹虚劳', 2, 9);
INSERT INTO `typhoidtheoryprescriptionworkcited` VALUES (16, '*、《外台》炙甘草汤，治肺痿涎唾多，心中温温液液者。', '金匮要略7 肺痿肺痈咳嗽上气', 2, 9);
INSERT INTO `typhoidtheoryprescriptionworkcited` VALUES (17, '29、伤寒脉浮。自汗出。小便数。心烦。微恶寒。脚挛急。反与桂枝。欲攻其表。此误也。得之便厥。咽中干。烦躁吐逆者。作甘草干姜汤与之。以复其阳。若厥愈。足温者。更作芍药甘草汤与之。其脚即伸。若胃气不和。谵语者。少与调胃承气汤。若重发汗。复加烧针者。四逆汤主之。', '辨太阳病脉证并治（上）第五', 1, 10);
INSERT INTO `typhoidtheoryprescriptionworkcited` VALUES (18, '91、伤寒。医下之。续得下利。清谷不止。身疼痛者。急当救里。后身疼痛。清便自调者。急当救表。救里。宜四逆汤。救表。宜桂枝汤。', '辨太阳病脉证并治（中）第六', 1, 10);
INSERT INTO `typhoidtheoryprescriptionworkcited` VALUES (19, '92、病发热。头痛。脉反沉。若不差。身体疼痛。当救其里。四逆汤方。', '辨太阳病脉证并治（中）第六', 1, 10);
INSERT INTO `typhoidtheoryprescriptionworkcited` VALUES (20, '225、脉浮而迟。表热里寒。下利清谷者。四逆汤主之。', '辨阳明病脉证并治第八', 1, 10);
INSERT INTO `typhoidtheoryprescriptionworkcited` VALUES (21, '323、少阴病。脉沉者。急温之。宜四逆汤。', '辨少阴病脉证并治第十一', 1, 10);
INSERT INTO `typhoidtheoryprescriptionworkcited` VALUES (22, '353、大汗出。热不去。内拘急。四肢疼。又下利厥逆而恶寒者。四逆汤主之。', '辨厥阴病脉证并治第十二', 1, 10);
INSERT INTO `typhoidtheoryprescriptionworkcited` VALUES (23, '354、大汗。若大下利而厥冷者。四逆汤主之。', '辨厥阴病脉证并治第十二', 1, 10);
INSERT INTO `typhoidtheoryprescriptionworkcited` VALUES (24, '372、下利腹胀满。身体疼痛者。先温其里。乃攻其表。温里。宜四逆汤。攻表。宜桂枝汤。', '辨厥阴病脉证并治第十二', 1, 10);
INSERT INTO `typhoidtheoryprescriptionworkcited` VALUES (25, '377、呕而脉弱。小便复利。身有微热。见厥者难治。四逆汤主之。', '辨厥阴病脉证并治第十二', 1, 10);
INSERT INTO `typhoidtheoryprescriptionworkcited` VALUES (26, '388、吐利汗出。发热恶寒。四肢拘急。手足厥冷者。四逆汤主之。', '辨霍乱病脉证并治第十三', 1, 10);
INSERT INTO `typhoidtheoryprescriptionworkcited` VALUES (27, '389、既吐且利。小便复利而大汗出。下利清谷。内寒外热。脉微欲绝者。四逆汤主之。', '辨霍乱病脉证并治第十三', 1, 10);
INSERT INTO `typhoidtheoryprescriptionworkcited` VALUES (28, '22、下利腹胀满。身体疼痛者。先温其里。乃攻其表。温里。宜四逆汤。攻表。宜桂枝汤。', '辨可发汗病脉证并治第十六', 1, 10);
INSERT INTO `typhoidtheoryprescriptionworkcited` VALUES (29, '13、伤寒脉浮自汗出。小便数。心烦。微恶寒。脚挛急。反与桂枝。欲攻其表。此误也。得之便厥。咽中干。烦躁吐逆者。作甘草干姜汤与之。以复其阳。若厥愈足温者。更作芍药甘草汤与之。其脚即伸。若胃气不和。讝语者。少与调胃承气汤。若重发汗。复加烧针者。与四逆汤。', '辨发汗后病脉证并治第十七', 1, 10);
INSERT INTO `typhoidtheoryprescriptionworkcited` VALUES (30, '31、大汗出。热不去。内拘急。四肢疼。又下利厥逆而恶寒者。属四逆汤。', '辨发汗后病脉证并治第十七', 1, 10);
INSERT INTO `typhoidtheoryprescriptionworkcited` VALUES (31, '44、大汗。若大下而厥冷者。属四逆汤。', '辨发汗吐下后病脉证并治第二十二', 1, 10);
INSERT INTO `typhoidtheoryprescriptionworkcited` VALUES (32, '55、伤寒医下之。续得下利。清谷不止。身疼痛者。急当救里。后身疼痛。清便自调者。急当救表。救里。宜四逆汤。救表。宜桂枝汤。', '辨发汗吐下后病脉证并治第二十二', 1, 10);
INSERT INTO `typhoidtheoryprescriptionworkcited` VALUES (33, '14、呕而脉弱，小便复利，身有微热，见厥者难治，四逆汤主之。', '金匮要略17 呕吐哕下利', 2, 10);
INSERT INTO `typhoidtheoryprescriptionworkcited` VALUES (34, '36、下利，腹胀满，身体疼痛者，先温其里，乃攻其表。温里宜四逆汤，攻表宜桂枝汤。', '金匮要略17 呕吐哕下利', 2, 10);
INSERT INTO `typhoidtheoryprescriptionworkcited` VALUES (35, '397、伤寒解后。虚羸少气。气逆欲吐。竹叶石膏汤主之。', '辨阴阳易差后劳复病脉证并治第十四', 1, 11);
INSERT INTO `typhoidtheoryprescriptionworkcited` VALUES (36, '390、吐已。下断。汗出而厥。四肢拘急不解。脉微欲绝者。通脉四逆加猪胆汁汤主之。', '辨霍乱病脉证并治第十三', 1, 12);
INSERT INTO `typhoidtheoryprescriptionworkcited` VALUES (37, '371、热利。下重者。白头翁汤主之。', '辨厥阴病脉证并治第十二', 1, 13);
INSERT INTO `typhoidtheoryprescriptionworkcited` VALUES (38, '373、下利。欲饮水者。以有热故也。白头翁汤主之。', '辨厥阴病脉证并治第十二', 1, 13);
INSERT INTO `typhoidtheoryprescriptionworkcited` VALUES (39, '43、热利下重者，白头翁汤主之。', '金匮要略17 呕吐哕下利', 2, 13);
INSERT INTO `typhoidtheoryprescriptionworkcited` VALUES (40, '318、少阴病。四逆。其人或咳。或悸。或小便不利。或腹中痛。或泄利下重者。四逆散主之。', '辨少阴病脉证并治第十一', 1, 14);
INSERT INTO `typhoidtheoryprescriptionworkcited` VALUES (41, '395、大病差后。从腰以下有水气者。牡蛎泽泻散主之。', '辨阴阳易差后劳复病脉证并治第十四', 1, 15);
INSERT INTO `typhoidtheoryprescriptionworkcited` VALUES (42, '392、伤寒阴易之为病。其人身体重。少气。少腹里急。或引阴中拘挛。热上冲胸。头重不欲举。眼中生花。膝胫拘急者。烧裈散主之。', '辨阴阳易差后劳复病脉证并治第十四', 1, 16);
INSERT INTO `typhoidtheoryprescriptionworkcited` VALUES (43, '386、霍乱。头痛发热。身疼痛。热多欲饮水者。五苓散主之。寒多不用水者。理中丸主之。', '辨霍乱病脉证并治第十三', 1, 17);
INSERT INTO `typhoidtheoryprescriptionworkcited` VALUES (44, '396、大病差后。喜唾。久不了了。胸上有寒。当以丸药温之。宜理中丸。', '辨阴阳易差后劳复病脉证并治第十四', 1, 17);
INSERT INTO `typhoidtheoryprescriptionworkcited` VALUES (45, '385、(康平本有“吐利”)恶寒。脉微而复利。利止。亡血也。四逆加人参汤主之。', '辨霍乱病脉证并治第十三', 1, 18);
INSERT INTO `typhoidtheoryprescriptionworkcited` VALUES (46, '359、伤寒。本自寒下。医反复吐下之。寒格。更逆吐下。若食入口即吐。干姜黄芩黄连人参汤主之。', '辨厥阴病脉证并治第十二', 1, 19);
INSERT INTO `typhoidtheoryprescriptionworkcited` VALUES (47, '73、伤寒本自寒。下医复吐下之。寒格更逆吐下。若食入口。即吐。属干姜黄芩黄连人参汤。', '辨发汗吐下后病脉证并治第二十二', 1, 19);
INSERT INTO `typhoidtheoryprescriptionworkcited` VALUES (48, '357、伤寒六七日。大下后。寸脉沉而迟。手足厥逆。下部脉不至。喉咽不利。唾脓血。泄利不止者。为难治。麻黄升麻汤主之。', '辨厥阴病脉证并治第十二', 1, 20);
INSERT INTO `typhoidtheoryprescriptionworkcited` VALUES (49, '72、伤寒六七日。大下。寸脉沉而迟。手足厥逆。下部脉不至。喉咽不利。唾脓血。泄利不止者。为难治。属麻黄升麻汤。', '辨发汗吐下后病脉证并治第二十二', 1, 20);
INSERT INTO `typhoidtheoryprescriptionworkcited` VALUES (50, '352、若其人内有久寒者。宜当归四逆加吴茱萸生姜汤。', '辨厥阴病脉证并治第十二', 1, 21);
INSERT INTO `typhoidtheoryprescriptionworkcited` VALUES (51, '351、手足厥寒。脉细欲绝者。当归四逆汤主之。', '辨厥阴病脉证并治第十二', 1, 22);
INSERT INTO `typhoidtheoryprescriptionworkcited` VALUES (52, '43、下利脉大者。虚也。以强下之故也。设脉浮革。因尔肠鸣者。属当归四逆汤。', '辨不可下病脉证并治第二十', 1, 22);
INSERT INTO `typhoidtheoryprescriptionworkcited` VALUES (53, '338、伤寒。脉微而厥。至七八日肤冷。其人躁无暂安时者。此为藏厥。非蚘厥也。蚘厥者。其人当吐蚘。令病者静。而复时烦者。此为藏寒。蚘上入其膈。故烦。须臾复止。得食而呕。又烦。烦者。蚘闻食臭出。其人常自吐蚘。蚘厥者。乌梅丸主之。又主久利。', '辨厥阴病脉证并治第十二', 1, 23);
INSERT INTO `typhoidtheoryprescriptionworkcited` VALUES (54, '8、蛔厥者，乌梅丸主之。', '金匮要略19 趺蹶手指臂肿转筋阴狐疝蛔虫', 2, 23);
INSERT INTO `typhoidtheoryprescriptionworkcited` VALUES (55, '317、少阴病。下利清谷。里寒外热。手足厥逆。脉微欲绝。身反不恶寒。其人面色赤。或腹痛。或干呕。或咽痛。或利止脉不出者。通脉四逆汤主之。', '辨少阴病脉证并治第十一', 1, 24);
INSERT INTO `typhoidtheoryprescriptionworkcited` VALUES (56, '370、下利清谷。里寒外热。汗出而厥者。通脉四逆汤主之。', '辨厥阴病脉证并治第十二', 1, 24);
INSERT INTO `typhoidtheoryprescriptionworkcited` VALUES (57, '45、下利清谷，里寒外热，汗出而厥者，通脉四逆汤主之。', '金匮要略17 呕吐哕下利', 2, 24);
INSERT INTO `typhoidtheoryprescriptionworkcited` VALUES (58, '21、少阴脉滑而数者，阴中即生疮，阴中蚀疮烂者，狼牙汤洗之。', '金匮要略22 妇人杂病', 2, 25);
INSERT INTO `typhoidtheoryprescriptionworkcited` VALUES (59, '20、蛇床子散方，温阴中坐药。', '金匮要略22 妇人杂病', 2, 26);
INSERT INTO `typhoidtheoryprescriptionworkcited` VALUES (60, '15、虚劳腰痛，少腹拘急，小便不利者，八味肾气丸主之。附方为肾气丸', '金匮要略6 血痹虚劳', 2, 27);
INSERT INTO `typhoidtheoryprescriptionworkcited` VALUES (61, '17、夫短气有微饮，当从小便去之，茯苓桂枝白术甘草汤主之，肾气丸亦主之。', '金匮要略12 痰饮咳嗽', 2, 27);
INSERT INTO `typhoidtheoryprescriptionworkcited` VALUES (62, '3、男子消渴，小便反多，以饮一斗，小便一斗，肾气丸主之。', '金匮要略13 消渴小便不利淋病', 2, 27);
INSERT INTO `typhoidtheoryprescriptionworkcited` VALUES (63, '19、问曰：妇人病，饮食如故，烦热不得卧，而反倚息者，何也？师曰：此名转胞，不得溺也。以胞系了戾，故致此病。但利小便则愈，宜肾气丸主之。', '金匮要略22 妇人杂病', 2, 27);
INSERT INTO `typhoidtheoryprescriptionworkcited` VALUES (64, '16、妇人六十二种风，及腹中血气刺痛，红蓝花酒主之。疑非仲景方。', '金匮要略22 妇人杂病', 2, 28);
INSERT INTO `typhoidtheoryprescriptionworkcited` VALUES (65, '15、妇人经水闭不利，脏坚癖不止，中有干血，下白物，矾石丸主之。', '金匮要略22 妇人杂病', 2, 29);
INSERT INTO `typhoidtheoryprescriptionworkcited` VALUES (66, '13、妇人少腹满如敦状，小便微难而不渴，生后者，此为水与血并结在血室也，大黄甘遂汤主之。', '金匮要略22 妇人杂病', 2, 30);
INSERT INTO `typhoidtheoryprescriptionworkcited` VALUES (67, '7、肝着，其人常欲蹈其胸上，先未苦时，但欲饮热，旋覆花汤主之。臣亿等校诸本旋覆花汤，皆同。同恐阙讹', '金匮要略11 五脏风寒积聚', 2, 31);
INSERT INTO `typhoidtheoryprescriptionworkcited` VALUES (68, '11、寸口脉弦而大，弦则为减，大则为芤，减则为寒，芤则为虚，寒虚相抟，此名曰革，妇人则半产漏下，旋覆花汤主之。', '金匮要略22 妇人杂病', 2, 31);
INSERT INTO `typhoidtheoryprescriptionworkcited` VALUES (69, '10、带下经水不利，少腹满痛，经一月再见者，土瓜根散主之。', '金匮要略22 妇人杂病', 2, 32);
INSERT INTO `typhoidtheoryprescriptionworkcited` VALUES (70, '9、问曰：妇人年五十所，病下利数十日不止，暮即发热，少腹里急，腹满，手掌烦热，唇口干燥，何也？师曰：此病属带下。何以故？曾经半产，瘀血在少腹不去，何以知之？其证唇口干燥，故知之。当以温经汤主之。', '金匮要略22 妇人杂病', 2, 33);
INSERT INTO `typhoidtheoryprescriptionworkcited` VALUES (71, '6、妇人脏躁，喜悲伤欲哭，象如神灵所作，数欠伸，甘麦大枣汤主之。', '金匮要略22 妇人杂病', 2, 34);
INSERT INTO `typhoidtheoryprescriptionworkcited` VALUES (72, '5、妇人咽中如有炙脔，半夏厚朴汤主之。', '金匮要略22 妇人杂病', 2, 35);
INSERT INTO `typhoidtheoryprescriptionworkcited` VALUES (73, '*、《千金》内补当归建中汤 治妇人产后虚羸不足，腹中刺痛不止，吸吸少气，或苦少腹中急，摩痛引腰者，不能食饮。产后一月，日得四五剂为善，令人强壮，宜。', '金匮要略21 妇人产后病', 2, 36);
INSERT INTO `typhoidtheoryprescriptionworkcited` VALUES (74, '*、附方《千金》三物黄芩汤 治妇人在草蓐，自发露得风。四肢苦烦热，头痛者，与小柴胡汤。头不痛但烦者，此汤主之。', '金匮要略21 妇人产后病', 2, 37);
INSERT INTO `typhoidtheorytype` VALUES (1, '伤寒论');
INSERT INTO `typhoidtheorytype` VALUES (2, '金匮要略');
INSERT INTO `typhoidtheorytype` VALUES (3, '中药');
INSERT INTO `typhoidtheorytype` VALUES (4, '原文摘要');
