INSERT INTO `sys_oper_log` VALUES (1642009787189846017, '药品说明', 2, 'com.xlbc.web.controller.app.ProductbasicinfoController.edit()', 'PUT', 1, 'admin', '', '/app/productbasicinfo', '127.0.0.1', '内网IP', '{\"images\":{\"image\":null,\"id\":6,\"productId\":1,\"ossIdList\":\"1642009687201832962,1642009736233246722,1642009750963642369,1642009764595130370,1642009779560407041\"},\"id\":1,\"productId\":1,\"commonName\":\"感冒灵颗粒\",\"brand\":\"999\",\"company\":\"华润三九医药股份有限公司\",\"approvalNumber\":\"国药准字Z44021940\",\"specification\":\"10g*9袋/盒\",\"dosageForm\":\"颗粒\",\"useMethod\":\"口服\",\"useDose\":\"一次10克（1袋），一日3次\",\"expirationDate\":\"24个月\",\"targetUser\":\"不限\",\"instructionBook\":\"【药品名称】\\n通用名称：感冒灵颗粒\\n商品名称：999\\n英文名称：GanMaoLingKeLi(999)\\n汉语拼音：Ganmaoling Keli\\n【成份】三叉苦、金盏银盘、野菊花、岗梅、咖啡因、对乙酰氨基酚、马来酸氯苯那敏、薄荷油。辅料为蔗糖粉。\\n【性状】本品为浅棕色至深棕色颗粒;味甜、微苦。\\n【功能主治】解热镇痛。用于感冒引起的头痛，发热，鼻塞。流涕，咽痛等。\\n【用法用量】开水冲服。一次10克(1袋)，一日3次。\\n【不良反应】偶见皮疹、荨麻疹、药热及粒细胞减少；可见困倦、嗜睡、口渴、虚弱感；长期大量用药会导致肝肾功能异常。\\n【禁忌】严重肝肾功能不全者禁用。\\n【注意事项】\\n1.忌烟、酒及辛辣、生冷、油腻食物。\\n2.不宜在服药期间同时服用滋补性中成药。\\n3.本品含对乙酰氨基酚、马来酸氯苯那敏、咖啡因。服用本品期间不得饮酒或含有酒精的饮料；不能同时服用与本品成份相似的其他抗感冒药；肝、肾功能不全者慎用；膀胱颈梗阻、甲状腺功能亢进、青光眼、高血压和前列腺肥大者慎用；孕妇及哺乳期妇女慎用；服药期间不得驾驶机、车、船、从事高空作业、机械作业及操作精密仪器。\\n4.脾胃虚寒，症见腹痛、喜暖、泄泻者慎用。\\n5.糖尿病患者及有心脏病等慢性病严重者应在医师指导下服用。\\n6.儿童、年老体弱者应在\\n【药物相互作用】\\n1、与其他解热镇痛药并用，有增加肾毒性的危险。\\n2、与其他药物同时使用可能会发生药物相互作用，详情请咨询医师或药师。\\n【药理作用】尚不明确\\n【贮藏】密封\\n【规格】每袋装10克(含对乙酰氨基酚0.2克)\\n【包装规格】每盒装9袋，复合膜\\n【有效期】24个月\\n【批准文号】国药准字Z44021940\\n【说明书修订日期】2007年05月15日\\n【生产企业】\\n企业名称：华润三九医药股份有限公司\\n企业简称：华润三九\"}', '{\"code\":200,\"msg\":\"操作成功\",\"data\":null}', 0, '', '2023-04-01 11:43:35');
