INSERT INTO `sys_oper_log` VALUES (1646429863316525058, '中成药数据', 2, 'com.xlbc.web.controller.app.ChinesepatentmedicineController.edit()', 'PUT', 1, 'admin', '', '/app/chinesepatentmedicine', '39.190.166.139', '浙江省 嘉兴市', '{\"id\":10,\"drugName\":\"二十五味珊瑚丸\",\"prescription\":\"珊瑚75g 珍珠15g 青金石20g 珍珠母50g 诃子100g 木香60g 红花80g 丁香 35g 沉香70g 朱砂30g 龙骨40g 炉甘石25g 脑石25g 磁石25g 禹粮土 25g 芝麻40g 葫芦30g 紫菀花45g 獐牙菜80g 藏菖蒲50g 榜那45g 打箭菊75g 甘草75g 西红花25g 人工麝香2g\",\"preparationMethod\":\"以上二十五味，除珊瑚、珍珠、西红花、人工麝香 外，其余珍珠母等二十一味粉碎成细粉，过筛；将珊瑚、珍珠、 西红花、人工麝香研细，与上述粉末配研，过筛，混匀，用水泛丸，阴干，即得。\",\"attending\":\"开窍，通络，止痛。用于“白脉病”，神志不清，身体麻木，头昏目弦，脑部疼痛，血压不调，头痛，癫痫及各种神经性疼痛。\",\"characters\":\"本品为红棕色的水丸；气微香，味甘、苦、涩。\",\"usageAndDosage\":\"开水泡服。一次1g，一日1次。\",\"notes\":null,\"specification\":\"(1）每4丸重1g(2）每丸重1g\",\"storeUp\":\"密封。\"}', '{\"code\":200,\"msg\":\"操作成功\",\"data\":null}', 0, '', '2023-04-13 16:27:23');
INSERT INTO `sys_oper_log` VALUES (1646429920501665793, '中成药数据', 2, 'com.xlbc.web.controller.app.ChinesepatentmedicineController.edit()', 'PUT', 1, 'admin', '', '/app/chinesepatentmedicine', '39.190.166.139', '浙江省 嘉兴市', '{\"id\":5,\"drugName\":\"乙肝宁颗粒\",\"prescription\":\"黄芪606g 白花蛇舌草408g 茵陈606g 金钱草408g 党参490g 蒲公英408g 制何首乌490g 牡丹皮408g 丹参490g 茯苓408g 白芍408g 白术408g 川楝子408g\",\"preparationMethod\":\"以上十三味，黄芪加水煎煮二次，每次2 小时，合并煎液，滤过，滤液浓缩至适量；茵陈提取挥发油，用倍他环糊精290g包合，备用；药渣与其余党参等十一味加水煎煮二次，每次2小时，合并煎液，滤过，滤液浓缩成相对密度约为1.25（20℃）的清膏，放冷，加乙醇使含醇量达55%，静置24小时，上清液回收乙醇至无醇味，加入上述黄芪浓缩液，浓缩至适量，加人明胶145g、甜菊素1.2g、乳糖162g及上述挥发油包合物，制成颗粒， 60℃以下干燥，制成1000g(含乳糖），或加入上述挥发油包合物及蔗糖5270g，制成颗粒，60℃以下干燥，制成5670g，即得。\",\"attending\":\"补气健脾，活血化瘀，清热解毒。用于慢性肝炎属脾气虚弱、血瘀阻络、湿热毒蕴证，症见胁痛、腹胀、乏力、尿黄；急性肝炎。\",\"characters\":\"本品为黄棕色至棕褐色的颗粒；味甜、微苦，或味甘、微苦(含乳糖）。\",\"usageAndDosage\":\"口服。一次1袋，一日3次；儿童酌减，治疗慢性肝炎者以3个月为一个疗程。\",\"notes\":\"服药期间忌食油腻、辛辣食物。\",\"specification\":\"（1）每袋装17g（2）每袋装3g(含乳糖）\",\"storeUp\":\"密封。\"}', '{\"code\":200,\"msg\":\"操作成功\",\"data\":null}', 0, '', '2023-04-13 16:27:37');
INSERT INTO `sys_oper_log` VALUES (1646430190749061122, '中成药数据', 1, 'com.xlbc.web.controller.app.ChinesepatentmedicineController.add()', 'POST', 1, 'admin', '', '/app/chinesepatentmedicine', '39.190.166.139', '浙江省 嘉兴市', '{\"id\":12,\"drugName\":\"二母宁嗽丸\",\"prescription\":\"川贝母225g 知母225g 石膏300g 炒栀子180g 黄芩180g 蜜桑白皮150g 茯苓150g 炒瓜蒌子150g 陈皮150g 麸炒枳实150g 炙甘草30g 五味子(蒸）30g\",\"preparationMethod\":\"以上十二味，粉碎成细粉，过筛，混匀。每100g粉末加炼蜜40～60g及适量水制成水蜜丸，干燥；或加炼蜜115～135g制成大蜜丸，即得。\",\"attending\":\"清肺润燥，化痰止咳。用于燥热蕴肺所致的咳嗽、痰黄而黏不易咳出、胸闷气促、久咳不止、声哑喉痛。\",\"characters\":\"本品为棕褐色的水蜜丸或大蜜丸；气微香，味甜，微苦。\",\"usageAndDosage\":\"口服。大蜜丸一次1丸，水蜜丸一次6g，—日2次。\",\"notes\":null,\"specification\":\"（1）大蜜丸每丸重9g (2）水蜜丸 每100丸重10g\",\"storeUp\":\"密封。\"}', '{\"code\":200,\"msg\":\"操作成功\",\"data\":null}', 0, '', '2023-04-13 16:28:41');
