INSERT INTO `sys_oper_log` VALUES (1646432794157748225, '经方', 1, 'com.xlbc.web.controller.app.TyphoidtheoryprescriptionController.add()', 'POST', 1, 'admin', '', '/app/typhoidtheoryprescription', '39.190.166.139', '浙江省 嘉兴市', '{\"workCites\":[{\"id\":12,\"content\":\"262、伤寒瘀热在里。身必黄。麻黄连轺赤小豆汤主之。\",\"provenance\":\"辨阳明病脉证并治第八\",\"type\":1,\"preparationId\":7}],\"id\":7,\"name\":\"麻黄连轺赤小豆汤\",\"type\":1,\"compose\":\"麻黄二两。去节 连轺二两(连翘根) 杏仁四十个。去皮尖 赤小豆一升 大枣十二枚。擘 生梓白皮一升。切 生姜二两。切 甘草二两。炙\",\"preparationMethod\":\"上八味。以潦水一斗。先煮麻黄。再沸。去上沫。内诸药。煮取三升。分温三服。半日服尽。\"}', '{\"code\":200,\"msg\":\"操作成功\",\"data\":null}', 0, '', '2023-04-13 16:39:02');
INSERT INTO `sys_oper_log` VALUES (1646433056767315969, '经方', 1, 'com.xlbc.web.controller.app.TyphoidtheoryprescriptionController.add()', 'POST', 1, 'admin', '', '/app/typhoidtheoryprescription', '39.190.166.139', '浙江省 嘉兴市', '{\"workCites\":[{\"id\":13,\"content\":\"393、大病差后。劳复者。枳实栀子豉汤主之。\",\"provenance\":\"辨阴阳易差后劳复病脉证并治第十四\",\"type\":1,\"preparationId\":8}],\"id\":8,\"name\":\"枳实栀子豉汤\",\"type\":1,\"compose\":\"枳实三枚。炙 栀子十四个。擘 豉一升(绵裹)\",\"preparationMethod\":\"上三味。以清浆水七升。空煮取四升。内枳实、栀子。煮取二升。下豉。更煮五六沸。去滓。温分再服。覆令微似汗。若有宿食者。内大黄如博棋子大五六枚。服之愈。\"}', '{\"code\":200,\"msg\":\"操作成功\",\"data\":null}', 0, '', '2023-04-13 16:40:05');
INSERT INTO `sys_oper_log` VALUES (1646433360430731266, '经方', 1, 'com.xlbc.web.controller.app.TyphoidtheoryprescriptionController.add()', 'POST', 1, 'admin', '', '/app/typhoidtheoryprescription', '39.190.166.139', '浙江省 嘉兴市', '{\"workCites\":[{\"id\":14,\"content\":\"177、伤寒。脉结代。心动悸。炙甘草汤主之。\",\"provenance\":\"辨太阳病脉证并治（下）第七\",\"type\":1,\"preparationId\":9},{\"id\":15,\"content\":\"*、附方《千金翼》炙甘草汤 一云复脉汤。治虚劳不足，汗出而闷，脉结悸，行动如常，不出百日，危急者，十一日死。\",\"provenance\":\"金匮要略6 血痹虚劳\",\"type\":2,\"preparationId\":9},{\"id\":16,\"content\":\"*、《外台》炙甘草汤，治肺痿涎唾多，心中温温液液者。\",\"provenance\":\"金匮要略7 肺痿肺痈咳嗽上气\",\"type\":2,\"preparationId\":9}],\"id\":9,\"name\":\"炙甘草汤\",\"type\":1,\"compose\":\"甘草四两。炙 生姜三两。切 人参二两 桂枝三两。去皮 生地黄一斤 阿胶二两 麦门冬半升(去心) 麻子仁半升 大枣三十枚。擘\",\"preparationMethod\":\"上九味。以清酒七升。水八升。先煮八味。取三升。去滓。内胶烊消尽。温服一升。日三服。一名复脉汤。\"}', '{\"code\":200,\"msg\":\"操作成功\",\"data\":null}', 0, '', '2023-04-13 16:41:17');
INSERT INTO `sys_oper_log` VALUES (1646434637973786626, '经方', 1, 'com.xlbc.web.controller.app.TyphoidtheoryprescriptionController.add()', 'POST', 1, 'admin', '', '/app/typhoidtheoryprescription', '39.190.166.139', '浙江省 嘉兴市', '{\"workCites\":[{\"id\":17,\"content\":\"29、伤寒脉浮。自汗出。小便数。心烦。微恶寒。脚挛急。反与桂枝。欲攻其表。此误也。得之便厥。咽中干。烦躁吐逆者。作甘草干姜汤与之。以复其阳。若厥愈。足温者。更作芍药甘草汤与之。其脚即伸。若胃气不和。谵语者。少与调胃承气汤。若重发汗。复加烧针者。四逆汤主之。\",\"provenance\":\"辨太阳病脉证并治（上）第五\",\"type\":1,\"preparationId\":10},{\"id\":18,\"content\":\"91、伤寒。医下之。续得下利。清谷不止。身疼痛者。急当救里。后身疼痛。清便自调者。急当救表。救里。宜四逆汤。救表。宜桂枝汤。\",\"provenance\":\"辨太阳病脉证并治（中）第六\",\"type\":1,\"preparationId\":10},{\"id\":19,\"content\":\"92、病发热。头痛。脉反沉。若不差。身体疼痛。当救其里。四逆汤方。\",\"provenance\":\"辨太阳病脉证并治（中）第六\",\"type\":1,\"preparationId\":10},{\"id\":20,\"content\":\"225、脉浮而迟。表热里寒。下利清谷者。四逆汤主之。\",\"provenance\":\"辨阳明病脉证并治第八\",\"type\":1,\"preparationId\":10},{\"id\":21,\"content\":\"323、少阴病。脉沉者。急温之。宜四逆汤。\",\"provenance\":\"辨少阴病脉证并治第十一\",\"type\":1,\"preparationId\":10},{\"id\":22,\"content\":\"353、大汗出。热不去。内拘急。四肢疼。又下利厥逆而恶寒者。四逆汤主之。\",\"provenance\":\"辨厥阴病脉证并治第十二\",\"type\":1,\"preparationId\":10},{\"id\":23,\"content\":\"354、大汗。若大下利而厥冷者。四逆汤主之。\",\"provenance\":\"辨厥阴病脉证并治第十二\",\"type\":1,\"preparationId\":10},{\"id\":24,\"content\":\"372、下利腹胀满。身体疼痛者。先温其里。乃攻其表。温里。宜四逆汤。攻表。宜桂枝汤。\",\"provenance\":\"辨厥阴病脉证并治第十二\",\"type\":1,\"preparationId\":10},{\"id\":25,\"content\":\"377、呕而脉弱。小便复利。身有微热。见厥者难治。四逆汤主之。\",\"provenance\":\"辨厥阴病脉证并治第十二\",\"type\":1,\"preparationId\":10},{\"id\":26,\"content\":\"388、吐利汗出。发热恶寒。四肢拘急。手足厥冷者。四逆汤主之。\",\"provenance\":\"辨霍乱病脉证并治第十三\",\"type\":1,\"preparationId\":10},{\"id\":27,\"content\":\"389、既吐且利。小便复利而大汗出。下利清谷。内寒外热。脉微欲绝者。四逆汤主之。\",\"provenance\":\"辨霍乱病脉证并治第十三\",\"type\":1,\"preparationId\":10},{\"id\":28,\"content\":\"22、下利腹胀满。身体疼痛者。先温其里。乃攻其表。温里。宜四逆汤。攻表。宜桂枝汤。\",\"provenance\":\"辨可发汗病脉证并治第十六\",\"type\":1,\"preparationId\":10},{\"id\":29,\"content\":\"13、伤寒脉浮自汗出。小便数。心烦。微恶寒。脚挛急。反与桂枝。欲攻其表。此误也。得之便厥。咽中干。烦躁吐逆者。作甘草干姜汤与之。以复其阳。若厥愈足温者。更作芍药甘草汤与之。其脚即伸。若胃气不和。讝语者。少与调胃承气汤。若重发汗。复加烧针者。与四逆汤。\",\"provenance\":\"辨发汗后病脉证并治第十七\",\"type\":1,\"preparationId\":10},{\"id\":30,\"content\":\"31、大汗出。热不去。内拘急。四肢疼。又下利厥逆而恶寒者。属四逆汤。\",\"provenance\":\"辨发汗后病脉证并治第十七\",\"type\":1,\"preparationId\":10},{\"id\":31,\"content\":\"44、大汗。若大下而厥冷者。属四逆汤。\",\"provenance\":\"辨发汗吐下后病脉证并治第二十二\",\"type\":1,\"preparationId\":10},{\"id\":32,\"content\":\"55、伤寒医下之。续得下利。清谷不止。身疼痛者。急当救里。后身疼痛。清便自调者。急当救表。救里。宜四逆汤。救表。宜桂枝汤。\",\"provenance\":\"辨发汗吐下后病脉证并治第二十二\"', '{\"code\":200,\"msg\":\"操作成功\",\"data\":null}', 0, '', '2023-04-13 16:46:22');
