INSERT INTO `sys_oper_log` VALUES (1646435267933081601, '经方', 1, 'com.xlbc.web.controller.app.TyphoidtheoryprescriptionController.add()', 'POST', 1, 'admin', '', '/app/typhoidtheoryprescription', '39.190.166.139', '浙江省 嘉兴市', '{\"workCites\":[{\"id\":36,\"content\":\"390、吐已。下断。汗出而厥。四肢拘急不解。脉微欲绝者。通脉四逆加猪胆汁汤主之。\",\"provenance\":\"辨霍乱病脉证并治第十三\",\"type\":1,\"preparationId\":12}],\"id\":12,\"name\":\"通脉四逆加猪胆汁汤\",\"type\":1,\"compose\":\"甘草二两。炙 附子大者一枚(生。去皮。破八片) 干姜三两。强人可四两 猪胆汁半合\",\"preparationMethod\":\"上四味。以水三升。煮取一升二合。去滓。内猪胆汁。分温再服。其脉即来。无猪胆。以羊胆代之。\"}', '{\"code\":200,\"msg\":\"操作成功\",\"data\":null}', 0, '', '2023-04-13 16:48:52');
INSERT INTO `sys_oper_log` VALUES (1646435526147018754, '经方', 1, 'com.xlbc.web.controller.app.TyphoidtheoryprescriptionController.add()', 'POST', 1, 'admin', '', '/app/typhoidtheoryprescription', '39.190.166.139', '浙江省 嘉兴市', '{\"workCites\":[{\"id\":37,\"content\":\"371、热利。下重者。白头翁汤主之。\",\"provenance\":\"辨厥阴病脉证并治第十二\",\"type\":1,\"preparationId\":13},{\"id\":38,\"content\":\"373、下利。欲饮水者。以有热故也。白头翁汤主之。\",\"provenance\":\"辨厥阴病脉证并治第十二\",\"type\":1,\"preparationId\":13},{\"id\":39,\"content\":\"43、热利下重者，白头翁汤主之。\",\"provenance\":\"金匮要略17 呕吐哕下利\",\"type\":2,\"preparationId\":13}],\"id\":13,\"name\":\"白头翁汤\",\"type\":1,\"compose\":\"白头翁二两 黄柏三两 黄连三两 秦皮三两\",\"preparationMethod\":\"上四味。以水七升。煮取二升。去滓。温服一升。不愈。更服一升。\"}', '{\"code\":200,\"msg\":\"操作成功\",\"data\":null}', 0, '', '2023-04-13 16:49:53');
INSERT INTO `sys_oper_log` VALUES (1646435776286920705, '经方', 1, 'com.xlbc.web.controller.app.TyphoidtheoryprescriptionController.add()', 'POST', 1, 'admin', '', '/app/typhoidtheoryprescription', '39.190.166.139', '浙江省 嘉兴市', '{\"workCites\":[{\"id\":40,\"content\":\"318、少阴病。四逆。其人或咳。或悸。或小便不利。或腹中痛。或泄利下重者。四逆散主之。\",\"provenance\":\"辨少阴病脉证并治第十一\",\"type\":1,\"preparationId\":14}],\"id\":14,\"name\":\"四逆散\",\"type\":1,\"compose\":\"甘草炙 枳实(破。水渍。炙干) 芍药 柴胡\",\"preparationMethod\":\"上四味。各十分。捣筛。白饮和服方寸匕。日三服。咳者。加五味子、干姜各五分。并主下利。悸者。加桂枝五分。小便不利者。加茯苓五分。腹中痛者。加附子一枚。炮令坼。泄利下重者。先以水五升。煮薤白三升。去滓。以散三方寸匕。内汤中。煮取一升半。分温再服。\"}', '{\"code\":200,\"msg\":\"操作成功\",\"data\":null}', 0, '', '2023-04-13 16:50:53');
INSERT INTO `sys_oper_log` VALUES (1646435945967489025, '经方', 1, 'com.xlbc.web.controller.app.TyphoidtheoryprescriptionController.add()', 'POST', 1, 'admin', '', '/app/typhoidtheoryprescription', '39.190.166.139', '浙江省 嘉兴市', '{\"workCites\":[{\"id\":41,\"content\":\"395、大病差后。从腰以下有水气者。牡蛎泽泻散主之。\",\"provenance\":\"辨阴阳易差后劳复病脉证并治第十四\",\"type\":1,\"preparationId\":15}],\"id\":15,\"name\":\"牡蛎泽泻散\",\"type\":1,\"compose\":\"牡蛎(熬) 泽泻 蜀漆(煖水洗。去腥) 葶苈子(熬) 商陆根(熬) 海藻(洗。去咸) 栝楼根各等分\",\"preparationMethod\":\"上七味。异捣。下筛为散。更于臼中治之。白饮和服方寸匕。日三服。小便利。止后服。\"}', '{\"code\":200,\"msg\":\"操作成功\",\"data\":null}', 0, '', '2023-04-13 16:51:33');
INSERT INTO `sys_oper_log` VALUES (1646436077656051713, '经方', 1, 'com.xlbc.web.controller.app.TyphoidtheoryprescriptionController.add()', 'POST', 1, 'admin', '', '/app/typhoidtheoryprescription', '39.190.166.139', '浙江省 嘉兴市', '{\"workCites\":[{\"id\":42,\"content\":\"392、伤寒阴易之为病。其人身体重。少气。少腹里急。或引阴中拘挛。热上冲胸。头重不欲举。眼中生花。膝胫拘急者。烧裈散主之。\",\"provenance\":\"辨阴阳易差后劳复病脉证并治第十四\",\"type\":1,\"preparationId\":16}],\"id\":16,\"name\":\"烧裈散\",\"type\":1,\"compose\":\"妇人中裈近隐处取烧作灰\",\"preparationMethod\":\"上一味。水服方寸匕。日三服。小便即利。阴头微肿。此为愈矣。妇人病取男子裈烧服。\"}', '{\"code\":200,\"msg\":\"操作成功\",\"data\":null}', 0, '', '2023-04-13 16:52:05');
INSERT INTO `sys_oper_log` VALUES (1646436285475426305, '经方', 1, 'com.xlbc.web.controller.app.TyphoidtheoryprescriptionController.add()', 'POST', 1, 'admin', '', '/app/typhoidtheoryprescription', '39.190.166.139', '浙江省 嘉兴市', '{\"workCites\":[{\"id\":43,\"content\":\"386、霍乱。头痛发热。身疼痛。热多欲饮水者。五苓散主之。寒多不用水者。理中丸主之。\",\"provenance\":\"辨霍乱病脉证并治第十三\",\"type\":1,\"preparationId\":17},{\"id\":44,\"content\":\"396、大病差后。喜唾。久不了了。胸上有寒。当以丸药温之。宜理中丸。\",\"provenance\":\"辨阴阳易差后劳复病脉证并治第十四\",\"type\":1,\"preparationId\":17}],\"id\":17,\"name\":\"理中丸\",\"type\":1,\"compose\":\"人参 干姜 甘草炙 白术各三两\",\"preparationMethod\":\"上四味。捣筛。蜜合为丸。如鸡子黄大。以沸汤数合。和一丸。研碎。温服之。日三四、夜二服。腹中未热。益至三四丸。然不及汤。汤法：以四物依两数切。用水八升。煮取三升。去滓。温服一升。日三服。若脐上筑者。肾气动也。去术加桂四两。吐多者。去术加生姜三两。下多者还用术。悸者。加茯苓二两。渴欲得水者。加术。足前成四两半。腹中痛者。加人参。足前成四两半。寒者。加干姜。足前成四两半。腹满者。去术。加附子一枚。服汤后。如食顷。饮热粥一升许。微自温。勿发揭衣被。\"}', '{\"code\":200,\"msg\":\"操作成功\",\"data\":null}', 0, '', '2023-04-13 16:52:54');
INSERT INTO `sys_oper_log` VALUES (1646436427347759105, '经方', 1, 'com.xlbc.web.controller.app.TyphoidtheoryprescriptionController.add()', 'POST', 1, 'admin', '', '/app/typhoidtheoryprescription', '39.190.166.139', '浙江省 嘉兴市', '{\"workCites\":[{\"id\":45,\"content\":\"385、(康平本有“吐利”)恶寒。脉微而复利。利止。亡血也。四逆加人参汤主之。\",\"provenance\":\"辨霍乱病脉证并治第十三\",\"type\":1,\"preparationId\":18}],\"id\":18,\"name\":\"四逆加人参汤\",\"type\":1,\"compose\":\"甘草二两。炙 附子一枚(生。去皮。破八片) 干姜一两半 人参一两\",\"preparationMethod\":\"上四味。以水三升。煮取一升二合。去滓。分温再服。\"}', '{\"code\":200,\"msg\":\"操作成功\",\"data\":null}', 0, '', '2023-04-13 16:53:28');
INSERT INTO `sys_oper_log` VALUES (1646436644012920833, '经方', 1, 'com.xlbc.web.controller.app.TyphoidtheoryprescriptionController.add()', 'POST', 1, 'admin', '', '/app/typhoidtheoryprescription', '39.190.166.139', '浙江省 嘉兴市', '{\"workCites\":[{\"id\":46,\"content\":\"359、伤寒。本自寒下。医反复吐下之。寒格。更逆吐下。若食入口即吐。干姜黄芩黄连人参汤主之。\",\"provenance\":\"辨厥阴病脉证并治第十二\",\"type\":1,\"preparationId\":19},{\"id\":47,\"content\":\"73、伤寒本自寒。下医复吐下之。寒格更逆吐下。若食入口。即吐。属干姜黄芩黄连人参汤。\",\"provenance\":\"辨发汗吐下后病脉证并治第二十二\",\"type\":1,\"preparationId\":19}],\"id\":19,\"name\":\"干姜黄芩黄连人参汤\",\"type\":1,\"compose\":\"干姜 黄芩 黄连 人参各三两\",\"preparationMethod\":\"上四味。以水六升。煮取二升。去滓。分温再服。\"}', '{\"code\":200,\"msg\":\"操作成功\",\"data\":null}', 0, '', '2023-04-13 16:54:20');
INSERT INTO `sys_oper_log` VALUES (1646436821360676866, '经方', 1, 'com.xlbc.web.controller.app.TyphoidtheoryprescriptionController.add()', 'POST', 1, 'admin', '', '/app/typhoidtheoryprescription', '39.190.166.139', '浙江省 嘉兴市', '{\"workCites\":[{\"id\":48,\"content\":\"357、伤寒六七日。大下后。寸脉沉而迟。手足厥逆。下部脉不至。喉咽不利。唾脓血。泄利不止者。为难治。麻黄升麻汤主之。\",\"provenance\":\"辨厥阴病脉证并治第十二\",\"type\":1,\"preparationId\":20},{\"id\":49,\"content\":\"72、伤寒六七日。大下。寸脉沉而迟。手足厥逆。下部脉不至。喉咽不利。唾脓血。泄利不止者。为难治。属麻黄升麻汤。\",\"provenance\":\"辨发汗吐下后病脉证并治第二十二\",\"type\":1,\"preparationId\":20}],\"id\":20,\"name\":\"麻黄升麻汤\",\"type\":1,\"compose\":\"麻黄二两半。去节 升麻一两一分 当归一两一分 知母十八铢 黄芩十八铢 葳蕤十八铢(一作菖蒲) 芍药六铢 天门冬六铢(去心) 桂枝六铢。去皮 茯苓六铢 甘草六铢。炙 石膏六铢(碎。绵裹) 白术六铢 干姜六铢\",\"preparationMethod\":\"上十四味。以水一斗。先煮麻黄一两沸。去上沫。内诸药。煮取三升。去滓。分温三服。相去如炊三斗米顷。令尽。汗出愈。\"}', '{\"code\":200,\"msg\":\"操作成功\",\"data\":null}', 0, '', '2023-04-13 16:55:02');
INSERT INTO `sys_oper_log` VALUES (1646437008934146050, '经方', 1, 'com.xlbc.web.controller.app.TyphoidtheoryprescriptionController.add()', 'POST', 1, 'admin', '', '/app/typhoidtheoryprescription', '39.190.166.139', '浙江省 嘉兴市', '{\"workCites\":[{\"id\":50,\"content\":\"352、若其人内有久寒者。宜当归四逆加吴茱萸生姜汤。\",\"provenance\":\"辨厥阴病脉证并治第十二\",\"type\":1,\"preparationId\":21}],\"id\":21,\"name\":\"当归四逆加吴茱萸生姜汤\",\"type\":1,\"compose\":\"当归三两 桂枝三两。去皮 芍药三两 细辛三两 甘草二两。炙 通草二两 大枣二十五枚。擘 生姜半斤 吴茱萸二升\",\"preparationMethod\":\"上九味。以水六升、清酒六升和。煮取五升。去滓。温分五服。一方。水、酒各四升。\"}', '{\"code\":200,\"msg\":\"操作成功\",\"data\":null}', 0, '', '2023-04-13 16:55:47');
INSERT INTO `sys_oper_log` VALUES (1646439373439471618, '经方', 1, 'com.xlbc.web.controller.app.TyphoidtheoryprescriptionController.add()', 'POST', 1, 'admin', '', '/app/typhoidtheoryprescription', '39.190.166.139', '浙江省 嘉兴市', '{\"workCites\":[{\"id\":51,\"content\":\"351、手足厥寒。脉细欲绝者。当归四逆汤主之。\",\"provenance\":\"辨厥阴病脉证并治第十二\",\"type\":1,\"preparationId\":22},{\"id\":52,\"content\":\"43、下利脉大者。虚也。以强下之故也。设脉浮革。因尔肠鸣者。属当归四逆汤。\",\"provenance\":\"辨不可下病脉证并治第二十\",\"type\":1,\"preparationId\":22}],\"id\":22,\"name\":\"当归四逆汤\",\"type\":1,\"compose\":\"当归三两 桂枝三两。去皮 芍药三两 细辛三两 甘草二两。炙 通草二两 大枣二十五枚(擘。一法十二枚)\",\"preparationMethod\":\"上七味。以水八升。煮取三升。去滓。温服一升。日三服。\"}', '{\"code\":200,\"msg\":\"操作成功\",\"data\":null}', 0, '', '2023-04-13 17:05:11');
INSERT INTO `sys_oper_log` VALUES (1646439571704221698, '经方', 1, 'com.xlbc.web.controller.app.TyphoidtheoryprescriptionController.add()', 'POST', 1, 'admin', '', '/app/typhoidtheoryprescription', '39.190.166.139', '浙江省 嘉兴市', '{\"workCites\":[{\"id\":53,\"content\":\"338、伤寒。脉微而厥。至七八日肤冷。其人躁无暂安时者。此为藏厥。非蚘厥也。蚘厥者。其人当吐蚘。令病者静。而复时烦者。此为藏寒。蚘上入其膈。故烦。须臾复止。得食而呕。又烦。烦者。蚘闻食臭出。其人常自吐蚘。蚘厥者。乌梅丸主之。又主久利。\",\"provenance\":\"辨厥阴病脉证并治第十二\",\"type\":1,\"preparationId\":23},{\"id\":54,\"content\":\"8、蛔厥者，乌梅丸主之。\",\"provenance\":\"金匮要略19 趺蹶手指臂肿转筋阴狐疝蛔虫\",\"type\":2,\"preparationId\":23}],\"id\":23,\"name\":\"乌梅丸\",\"type\":1,\"compose\":\"乌梅三百枚 细辛六两 干姜十两 黄连十六两 附子六两(炮。去皮) 当归四两 蜀椒四两(出汗) 桂枝六两。去皮 人参六两 黄柏六两\",\"preparationMethod\":\"上十味。异捣筛。合治之。以苦酒（即酸醋）渍乌梅一宿。去核。蒸之五斗米下。饭熟。捣成泥。和药令相得。内臼中。与蜜杵二千下。丸如梧桐子大。先食。饮服十丸。日三服。稍加至二十丸。禁生冷。滑物。臭食等。\"}', '{\"code\":200,\"msg\":\"操作成功\",\"data\":null}', 0, '', '2023-04-13 17:05:58');
INSERT INTO `sys_oper_log` VALUES (1646767472651767810, '经方', 1, 'com.xlbc.web.controller.app.TyphoidtheoryprescriptionController.add()', 'POST', 1, 'admin', '', '/app/typhoidtheoryprescription', '39.190.166.69', '浙江省 嘉兴市', '{\"workCites\":[{\"id\":55,\"content\":\"317、少阴病。下利清谷。里寒外热。手足厥逆。脉微欲绝。身反不恶寒。其人面色赤。或腹痛。或干呕。或咽痛。或利止脉不出者。通脉四逆汤主之。\",\"provenance\":\"辨少阴病脉证并治第十一\",\"type\":1,\"preparationId\":24},{\"id\":56,\"content\":\"370、下利清谷。里寒外热。汗出而厥者。通脉四逆汤主之。\",\"provenance\":\"辨厥阴病脉证并治第十二\",\"type\":1,\"preparationId\":24},{\"id\":57,\"content\":\"45、下利清谷，里寒外热，汗出而厥者，通脉四逆汤主之。\",\"provenance\":\"金匮要略17 呕吐哕下利\",\"type\":2,\"preparationId\":24}],\"id\":24,\"name\":\"通脉四逆汤\",\"type\":1,\"compose\":\"甘草二两。炙 附子大者一枚(生用。去皮。破八片) 干姜三两。强人可四两\",\"preparationMethod\":\"上三味。以水三升。煮取一升二合。去滓。分温再服。其脉即出者愈。面色赤者。加葱九茎。腹中痛者。去葱。加芍药二两。呕者。加生姜二两。咽痛者。去芍药。加桔梗一两。利止脉不出者。去桔梗。加人参二两。病皆与方相应者。乃服之。\"}', '{\"code\":200,\"msg\":\"操作成功\",\"data\":null}', 0, '', '2023-04-14 14:48:56');
INSERT INTO `sys_oper_log` VALUES (1646767717854973953, '经方', 1, 'com.xlbc.web.controller.app.TyphoidtheoryprescriptionController.add()', 'POST', 1, 'admin', '', '/app/typhoidtheoryprescription', '39.190.166.69', '浙江省 嘉兴市', '{\"workCites\":[{\"id\":58,\"content\":\"21、少阴脉滑而数者，阴中即生疮，阴中蚀疮烂者，狼牙汤洗之。\",\"provenance\":\"金匮要略22 妇人杂病\",\"type\":2,\"preparationId\":25}],\"id\":25,\"name\":\"狼牙汤\",\"type\":2,\"compose\":\"狼牙三两\",\"preparationMethod\":\"右一味，以水四升，煮取半升，以绵缠筋如茧，浸汤沥阴中，日四遍。\"}', '{\"code\":200,\"msg\":\"操作成功\",\"data\":null}', 0, '', '2023-04-14 14:49:54');
INSERT INTO `sys_oper_log` VALUES (1646767886814121986, '经方', 1, 'com.xlbc.web.controller.app.TyphoidtheoryprescriptionController.add()', 'POST', 1, 'admin', '', '/app/typhoidtheoryprescription', '39.190.166.69', '浙江省 嘉兴市', '{\"workCites\":[{\"id\":59,\"content\":\"20、蛇床子散方，温阴中坐药。\",\"provenance\":\"金匮要略22 妇人杂病\",\"type\":2,\"preparationId\":26}],\"id\":26,\"name\":\"蛇床子散\",\"type\":2,\"compose\":\"蛇床子仁\",\"preparationMethod\":\"右一味，末之，以白粉少许，和令相得，如枣大，绵裹内之，自然温。\"}', '{\"code\":200,\"msg\":\"操作成功\",\"data\":null}', 0, '', '2023-04-14 14:50:34');
INSERT INTO `sys_oper_log` VALUES (1646768140867309570, '经方', 1, 'com.xlbc.web.controller.app.TyphoidtheoryprescriptionController.add()', 'POST', 1, 'admin', '', '/app/typhoidtheoryprescription', '39.190.166.69', '浙江省 嘉兴市', '{\"workCites\":[{\"id\":60,\"content\":\"15、虚劳腰痛，少腹拘急，小便不利者，八味肾气丸主之。附方为肾气丸\",\"provenance\":\"金匮要略6 血痹虚劳\",\"type\":2,\"preparationId\":27},{\"id\":61,\"content\":\"17、夫短气有微饮，当从小便去之，茯苓桂枝白术甘草汤主之，肾气丸亦主之。\",\"provenance\":\"金匮要略12 痰饮咳嗽\",\"type\":2,\"preparationId\":27},{\"id\":62,\"content\":\"3、男子消渴，小便反多，以饮一斗，小便一斗，肾气丸主之。\",\"provenance\":\"金匮要略13 消渴小便不利淋病\",\"type\":2,\"preparationId\":27},{\"id\":63,\"content\":\"19、问曰：妇人病，饮食如故，烦热不得卧，而反倚息者，何也？师曰：此名转胞，不得溺也。以胞系了戾，故致此病。但利小便则愈，宜肾气丸主之。\",\"provenance\":\"金匮要略22 妇人杂病\",\"type\":2,\"preparationId\":27}],\"id\":27,\"name\":\"肾气丸\",\"type\":2,\"compose\":\"干地黄八两，薯蓣四两，山茱萸四两，泽泻三两，茯苓三两，牡丹皮三两，桂枝，附子炮，各一两\",\"preparationMethod\":\"右八味，末之，炼蜜和丸梧子大。酒下十五丸，加至二十五丸，日再服。\"}', '{\"code\":200,\"msg\":\"操作成功\",\"data\":null}', 0, '', '2023-04-14 14:51:35');
INSERT INTO `sys_oper_log` VALUES (1646768294571773953, '经方', 1, 'com.xlbc.web.controller.app.TyphoidtheoryprescriptionController.add()', 'POST', 1, 'admin', '', '/app/typhoidtheoryprescription', '39.190.166.69', '浙江省 嘉兴市', '{\"workCites\":[{\"id\":64,\"content\":\"16、妇人六十二种风，及腹中血气刺痛，红蓝花酒主之。疑非仲景方。\",\"provenance\":\"金匮要略22 妇人杂病\",\"type\":2,\"preparationId\":28}],\"id\":28,\"name\":\"红蓝花酒\",\"type\":2,\"compose\":\"红蓝花一两\",\"preparationMethod\":\"右一味，以酒一大升，煎减半。顿服一半，未止，再服。\"}', '{\"code\":200,\"msg\":\"操作成功\",\"data\":null}', 0, '', '2023-04-14 14:52:12');
INSERT INTO `sys_oper_log` VALUES (1646768431746486273, '经方', 1, 'com.xlbc.web.controller.app.TyphoidtheoryprescriptionController.add()', 'POST', 1, 'admin', '', '/app/typhoidtheoryprescription', '39.190.166.69', '浙江省 嘉兴市', '{\"workCites\":[{\"id\":65,\"content\":\"15、妇人经水闭不利，脏坚癖不止，中有干血，下白物，矾石丸主之。\",\"provenance\":\"金匮要略22 妇人杂病\",\"type\":2,\"preparationId\":29}],\"id\":29,\"name\":\"矾石丸\",\"type\":2,\"compose\":\"矾石三分，烧，杏仁一分\",\"preparationMethod\":\"右二味，末之，炼蜜和丸枣核大，内脏中，剧者再内之。\"}', '{\"code\":200,\"msg\":\"操作成功\",\"data\":null}', 0, '', '2023-04-14 14:52:44');
INSERT INTO `sys_oper_log` VALUES (1646768555189047297, '经方', 1, 'com.xlbc.web.controller.app.TyphoidtheoryprescriptionController.add()', 'POST', 1, 'admin', '', '/app/typhoidtheoryprescription', '39.190.166.69', '浙江省 嘉兴市', '{\"workCites\":[{\"id\":66,\"content\":\"13、妇人少腹满如敦状，小便微难而不渴，生后者，此为水与血并结在血室也，大黄甘遂汤主之。\",\"provenance\":\"金匮要略22 妇人杂病\",\"type\":2,\"preparationId\":30}],\"id\":30,\"name\":\"大黄甘遂汤\",\"type\":2,\"compose\":\"大黄四两，甘遂二两，阿胶二两\",\"preparationMethod\":\"右三味，以水三升，煮取一升。顿服之，其血当下。\"}', '{\"code\":200,\"msg\":\"操作成功\",\"data\":null}', 0, '', '2023-04-14 14:53:14');
INSERT INTO `sys_oper_log` VALUES (1646768824899571714, '经方', 1, 'com.xlbc.web.controller.app.TyphoidtheoryprescriptionController.add()', 'POST', 1, 'admin', '', '/app/typhoidtheoryprescription', '39.190.166.69', '浙江省 嘉兴市', '{\"workCites\":[{\"id\":67,\"content\":\"7、肝着，其人常欲蹈其胸上，先未苦时，但欲饮热，旋覆花汤主之。臣亿等校诸本旋覆花汤，皆同。同恐阙讹\",\"provenance\":\"金匮要略11 五脏风寒积聚\",\"type\":2,\"preparationId\":31},{\"id\":68,\"content\":\"11、寸口脉弦而大，弦则为减，大则为芤，减则为寒，芤则为虚，寒虚相抟，此名曰革，妇人则半产漏下，旋覆花汤主之。\",\"provenance\":\"金匮要略22 妇人杂病\",\"type\":2,\"preparationId\":31}],\"id\":31,\"name\":\"旋覆花汤\",\"type\":2,\"compose\":\"旋覆花三两，葱十四茎，新绛少许\",\"preparationMethod\":\"右三味，以水三升，煮取一升，顿服之。\"}', '{\"code\":200,\"msg\":\"操作成功\",\"data\":null}', 0, '', '2023-04-14 14:54:18');
INSERT INTO `sys_oper_log` VALUES (1646769124674867202, '经方', 1, 'com.xlbc.web.controller.app.TyphoidtheoryprescriptionController.add()', 'POST', 1, 'admin', '', '/app/typhoidtheoryprescription', '39.190.166.69', '浙江省 嘉兴市', '{\"workCites\":[{\"id\":69,\"content\":\"10、带下经水不利，少腹满痛，经一月再见者，土瓜根散主之。\",\"provenance\":\"金匮要略22 妇人杂病\",\"type\":2,\"preparationId\":32}],\"id\":32,\"name\":\"土瓜根散\",\"type\":2,\"compose\":\"土瓜根，芍药，桂枝，䗪虫各三分\",\"preparationMethod\":\"右四味，杵为散。酒服方寸匕，日三服。\"}', '{\"code\":200,\"msg\":\"操作成功\",\"data\":null}', 0, '', '2023-04-14 14:55:29');
INSERT INTO `sys_oper_log` VALUES (1646769252873768962, '经方', 1, 'com.xlbc.web.controller.app.TyphoidtheoryprescriptionController.add()', 'POST', 1, 'admin', '', '/app/typhoidtheoryprescription', '39.190.166.69', '浙江省 嘉兴市', '{\"workCites\":[{\"id\":70,\"content\":\"9、问曰：妇人年五十所，病下利数十日不止，暮即发热，少腹里急，腹满，手掌烦热，唇口干燥，何也？师曰：此病属带下。何以故？曾经半产，瘀血在少腹不去，何以知之？其证唇口干燥，故知之。当以温经汤主之。\",\"provenance\":\"金匮要略22 妇人杂病\",\"type\":2,\"preparationId\":33}],\"id\":33,\"name\":\"温经汤\",\"type\":2,\"compose\":\"吴茱萸三两，当归，芎䓖，芍药各二两，人参，桂枝，阿胶，牡丹去心，生姜，甘草各二两，半夏半升，麦门冬一升，去心\",\"preparationMethod\":\"右十二味，以水一斗，煮取三升，分温三服。亦主妇人少腹寒，久不受胎，兼取崩中去血，或月水来过多，及至期不来。\"}', '{\"code\":200,\"msg\":\"操作成功\",\"data\":null}', 0, '', '2023-04-14 14:56:00');
INSERT INTO `sys_oper_log` VALUES (1646769380225421313, '经方', 1, 'com.xlbc.web.controller.app.TyphoidtheoryprescriptionController.add()', 'POST', 1, 'admin', '', '/app/typhoidtheoryprescription', '39.190.166.69', '浙江省 嘉兴市', '{\"workCites\":[{\"id\":71,\"content\":\"6、妇人脏躁，喜悲伤欲哭，象如神灵所作，数欠伸，甘麦大枣汤主之。\",\"provenance\":\"金匮要略22 妇人杂病\",\"type\":2,\"preparationId\":34}],\"id\":34,\"name\":\"甘麦大枣汤\",\"type\":2,\"compose\":\"甘草三两，小麦一升，大枣十枚\",\"preparationMethod\":\"右三味，以水六升，煮取三升，温分三服。亦补脾气。\"}', '{\"code\":200,\"msg\":\"操作成功\",\"data\":null}', 0, '', '2023-04-14 14:56:30');
INSERT INTO `sys_oper_log` VALUES (1646769504959827970, '经方', 1, 'com.xlbc.web.controller.app.TyphoidtheoryprescriptionController.add()', 'POST', 1, 'admin', '', '/app/typhoidtheoryprescription', '39.190.166.69', '浙江省 嘉兴市', '{\"workCites\":[{\"id\":72,\"content\":\"5、妇人咽中如有炙脔，半夏厚朴汤主之。\",\"provenance\":\"金匮要略22 妇人杂病\",\"type\":2,\"preparationId\":35}],\"id\":35,\"name\":\"半夏厚朴汤\",\"type\":2,\"compose\":\"半夏一升，厚朴三两，茯苓四两，生姜五两，干苏叶二两\",\"preparationMethod\":\"右五味，以水七升，煮取四升。分温四服，日三、夜一服。\"}', '{\"code\":200,\"msg\":\"操作成功\",\"data\":null}', 0, '', '2023-04-14 14:57:00');
INSERT INTO `sys_oper_log` VALUES (1646769637046849537, '经方', 1, 'com.xlbc.web.controller.app.TyphoidtheoryprescriptionController.add()', 'POST', 1, 'admin', '', '/app/typhoidtheoryprescription', '39.190.166.69', '浙江省 嘉兴市', '{\"workCites\":[{\"id\":73,\"content\":\"*、《千金》内补当归建中汤 治妇人产后虚羸不足，腹中刺痛不止，吸吸少气，或苦少腹中急，摩痛引腰者，不能食饮。产后一月，日得四五剂为善，令人强壮，宜。\",\"provenance\":\"金匮要略21 妇人产后病\",\"type\":2,\"preparationId\":36}],\"id\":36,\"name\":\"当归建中汤\",\"type\":2,\"compose\":\"当归四两，桂枝三两，芍药六两，生姜三两，甘草二两，大枣十二枚\",\"preparationMethod\":\"右六味，以水一斗，煮取三升。分温三服，一日令尽。若大虚，加饴糖六两，汤成，内之于火上暖，令饴消。若去血过多，崩伤内衄不止，加地黄六两、阿胶二两，合八味，汤成，内阿胶。若无当归，以芎䓖代之；若无生姜，以干姜代之。\"}', '{\"code\":200,\"msg\":\"操作成功\",\"data\":null}', 0, '', '2023-04-14 14:57:32');
INSERT INTO `sys_oper_log` VALUES (1646769809504047106, '经方', 1, 'com.xlbc.web.controller.app.TyphoidtheoryprescriptionController.add()', 'POST', 1, 'admin', '', '/app/typhoidtheoryprescription', '39.190.166.69', '浙江省 嘉兴市', '{\"workCites\":[{\"id\":74,\"content\":\"*、附方《千金》三物黄芩汤 治妇人在草蓐，自发露得风。四肢苦烦热，头痛者，与小柴胡汤。头不痛但烦者，此汤主之。\",\"provenance\":\"金匮要略21 妇人产后病\",\"type\":2,\"preparationId\":37}],\"id\":37,\"name\":\"三物黄芩汤\",\"type\":2,\"compose\":\"黄芩一两，苦参二两，干地黄四两\",\"preparationMethod\":\"右三味，以水八升，煮取二升。温服一升，多吐下虫。\"}', '{\"code\":200,\"msg\":\"操作成功\",\"data\":null}', 0, '', '2023-04-14 14:58:13');
