INSERT INTO `sys_oper_log` VALUES (1646789955652128769, '中药', 1, 'com.xlbc.web.controller.app.TyphoidtheorychineseherbController.add()', 'POST', 1, 'admin', '', '/app/typhoidtheorychineseherb', '39.190.166.69', '浙江省 嘉兴市', '{\"abstracts\":[{\"id\":51,\"cbId\":15,\"typeId\":4,\"content\":\"《神农本草经》\\n【白薇】味苦。平。主治暴中风。身热。肢满。忽忽不知人。狂惑邪气。寒热酸疼。温疟洗洗发作有时。生平原川谷。\"},{\"id\":52,\"cbId\":15,\"typeId\":4,\"content\":\"《名医别录》\\n【白薇】味咸。大寒。无毒。主治伤中淋露。下水气。利阴气。益精。一名白幕。一名薇草。一名春草。一名骨美。久服利人。生平原。三月三日采根。阴干。（恶黄芪干姜干漆山茱萸大枣。）\"}],\"cites\":[{\"id\":53,\"cbId\":15,\"typeId\":2,\"content\":\"<fj>竹皮大丸</fj>（一分(有热者倍之)）\"}],\"id\":15,\"name\":\"白薇\"}', '{\"code\":200,\"msg\":\"操作成功\",\"data\":null}', 0, '', '2023-04-14 16:18:16');
